function Form_OnLoadCompleted(obj)
{
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
		
	var gubunnm = "";
	if (gubun == "1"){
		gubunnm = "(전임)";
	}else if (gubun == "2"){
		gubunnm = "(겸임)";
	}else if (gubun == "3"){
		gubunnm = "(초빙강사)";
	}
	txtName.Text = name+" "+jikwi+gubunnm;
	calDateFr.Text = datefr;
	calDateTo.Text = dateto;
    
    btnSearch_OnClick();    
   
}

function btnSearch_OnClick(obj)
{
	
	tit_clearActionInfo(this);
	
	//TAB_Training.TabIndex = 1; //조회버튼 선택시  나오게 한다.
		tit_addSearchActionInfo("profes:searchProfesPopup0001StatPerson", false);
	ds_SearchJogeon.DeleteAll(); 		ds_SearchJogeon.AddRow();
	ds_SearchJogeon.setColumn(0, "STARTDATE", datefr_Val);	
	ds_SearchJogeon.setColumn(0, "ENDDATE", dateto_Val);			
	ds_SearchJogeon.setColumn(0, "SABUN", sabun_Val);	
	ds_SearchJogeon.setColumn(0, "EDUGUBUN", cbxEduGubun.Value);	
    				
	tit_callService(  
	                 ""
	               , ""  
	               , "ds_iProfesList=ds_SearchJogeon"//inDs 
	               , "ds_ProfesList=ds_oProfesList" //outDs
	               , ""
	               , "afterSearch"  //callBack
	               , false
	               ); 
}

