function lfn_Print(obj)
{
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    lfn_search();
    
    var result = 0;
	result+=ds_ioTrainingResult.count;
	result+=ds_ioTrainingResult2.count;
	result+=ds_ioTrainingResult3.count;
	result+=ds_ioTrainingResult4.count;
    result+=ds_ioTrainingResult5.count;
//    result+=ds_ioTrainingResult6.count;
    if(result < 1){
        alert("출력할 자료가 없습니다.");        
        cbxJibu.SetFocus();
        return ;
    }
    else{
        gfn_CallReportPopup("frmspcledu0830R01ProcStatistics.rex"
                        , "ds_ioTrainingResult||ds_ioTrainingResult2||ds_ioTrainingResult3||ds_ioTrainingResult4||ds_ioTrainingResult5||ds_ioTrainingResult6"
                        , "COURSE=" + quote(cbxCourse.text)
                         +"^EDORDER=" + quote(edOrderStart.text)
                         +"^JIBU=" + quote(cbxJibu.text)
                       ,true);
    }

}

//조회
function lfn_search(obj){
    search01();
    if (returnval == 0) {
        return false; 
    }    
    search02();
    if (returnval == 0) {
        return false; 
    }        
    search03();
    if (returnval == 0) {
        return false; 
    }        
    search04();
    if (returnval == 0) {
        return false; 
    }        
    search05();
    if (returnval == 0) {
        return false; 
    }    
    var result = 0;
	result+=ds_ioTrainingResult.count;
	result+=ds_ioTrainingResult2.count;
	result+=ds_ioTrainingResult3.count;
	result+=ds_ioTrainingResult4.count;
    result+=ds_ioTrainingResult5.count;
    if(result > 1){
        search06();
    }
}

//01.종합보고
function search01(obj){
    ds_ioTrainingResult.ClearData();
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("spcledu:searchTraining0830Result01", false);// 조회 트랜잭션	
	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,"ds_ioTrainingResult=ds_oResult"	// outDs  
	    ,"TOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부
            +" TOYEAR=" + quote(edYear.text)  //-- 년도
            +" TOTCCOURSECD=" + quote(cbxCourse.value) // -- 직능            
            +" TOTRAININGORDER1=" + quote(edOrderStart.text) // -- 회차시작일              
            +" MUNJEGUBUN=" + quote(radMunjeGubun.Value) // -- 면제구분
           // args 
		,"lfn_AfterSearch01"
		,false
		,""
		,true
	);
}

function lfn_AfterSearch01(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        returnval = 0;
        return false;
    }
        returnval = 1;    
}

