//06.수첩발급현황
function search06(obj){   

    var LICENSENO = edYear.text         //년도
               + "-" + mid(cbxJibu.value, 1,2) //지부
               + "-" + cbxCourse.value  //직능
               + "-";   //강습수료만
    ds_ioTrainingResult6.ClearData();
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("spcledu:searchTraining0830Result06", false);// 조회 트랜잭션	
	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,"ds_ioTrainingResult6=ds_oResult"	// outDs  
	    ,"LICENSENO=" + quote(LICENSENO)
	     +"TGTMGNO=" + quote(cbxJibu.value)   //-- 지부
            +" TYEAR=" + quote(edYear.text)  //-- 년도
            +" TCCOURSECD=" + quote(cbxCourse.value) // -- 직능            
            +" TRAININGORDER1=" + quote(edOrderStart.text) // -- 회차시작일
		,"lfn_AfterSearch01"
		,false
		,""
		,true
	);
}


