var ls_UploadFileName = "MAP"; //서버에 저장될 파일 명

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    gfn_SearchJibu(cbxJibu,"Y","전체");
    cbxRegion.Value = "";
    lfn_Search();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {

    if (gs_IsResizingForm == "Y") {
        gdEducationPlace.Height = nCy - gdEducationPlace.Top - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    ds_ioEducationPlace.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("spcledu:searchEducationPlace", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioEducationPlace=ds_oEducationPlace"	// outDs
		,"EPMGGTMGNO=" + quote(cbxJibu.Value)
            + " EPRREGIONCD=" + quote(cbxRegion.value)
            + " EPNM=" + quote(edEducationPlaceName.Text)	// args 
		,"lfn_AfterSearchEducationPlace"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {

	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

    lfn_Cancel(); // 값 초기화

    btnGenerateSeat.Visible = false;

	mb_IsNewData = true; // 저장버튼이 클릭 설정

    // 기본값 설정
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {

    if (ds_ioEducationPlace.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}

    ds_ioEducationPlace.DeleteRow(ds_ioEducationPlace.currow); // 선택된 ROW를 삭제한다.

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "spcledu:deleteEducationPlace"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iEducationPlace=ds_ioEducationPlace:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveEducationPlace"
		,false
	);
/*
    if (length(ls_RealFileName) >= 1) { //첨부 파일이 존재할 경우

        tit_FileDelete( //파일 삭제
            ls_FileUploadPath
            , ls_RealFileName
            , false
        );

        ls_RealFileName = "";
    }
*/
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {

    var nRow = 0; //추가인지 수정인지 구분
    var ls_UploadRealName = "";

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    if (mb_IsNewData) { // 신규 저장

        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
            return false;
        }

        ds_ioEducationPlace.InsertRow(0);

    } else { // 수정 저장

        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
            return false;
        }
        nRow = ds_ioEducationPlace.currow;

    }

    // 입력된 값을 그리드로 복사한다.
    ds_ioEducationPlace.SetColumn(nRow, "FILECHECK", "");
    ds_ioEducationPlace.SetColumn(nRow, "EPMGGTMGNO", cbxJibu.Value);
    ds_ioEducationPlace.SetColumn(nRow, "EPRREGIONCD", cbxRegion.Value);
    ds_ioEducationPlace.SetColumn(nRow, "EPNM", edEducationPlaceName.Text);
    ds_ioEducationPlace.SetColumn(nRow, "EPPARKINGINFO", edParkingInfo.Text);

//    if(lb_FileUpload) { //파일 업로드일 경우

//        ls_UploadRealName = ls_UploadFileName + "_" + Timem() + gfn_GetFileExtensionName(fdImageFile.FileName);

        ds_ioEducationPlace.SetColumn(nRow, "FILECHECK", "U");
        ds_ioEducationPlace.SetColumn(nRow, "EPSAVENM", edImageFile.Text);
        ds_ioEducationPlace.SetColumn(nRow, "EPREALNM", edImageFile.Text);/* ls_UploadRealName */
        ds_ioEducationPlace.SetColumn(nRow, "EPDIRPATH", ls_FileUploadPath);
//    }

    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "spcledu:insertEducationPlace"	// insert
        , "spcledu:updateEducationPlace"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iEducationPlace=ds_ioEducationPlace:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveEducationPlace"
        ,false
    );
/*
    if (lb_FileUpload) { //파일 변경일 경우

        if (Length(fdImageFile.FileName) < 1) { //삭제 일 경우

            tit_FileDelete(
                ls_FileUploadPath
                , ls_RealFileName
                , false
            );

        } else { //변경일 경우

            tit_FileWrite(
                hfImageFile
                , ls_FileUploadPath
                , fdImageFile.FilePath
                , fdImageFile.FileName
                , ls_UploadRealName
                , "U"
                , true
                , ls_RealFileName
            );

            hfImageFile.CloseFile();

        }
    }
*/
    mb_IsNewData = false;
}

/**
 * 엑셀 출력
 */
function lfn_Excel(obj) {
    if (ds_ioEducationPlace.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/*
 * 기능 : 취소
 */
function lfn_Cancel(obj) {
    gfn_FormInit(this);
    mb_IsNewData = true;
    ls_RealFileName = "";

}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 교육장 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEducationPlace(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdEducationPlace); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

    if(ds_ioEducationPlace.GetRowCount() > 0) {
		btnGenerateSeat.Enable = true;
		btnViewSeat.Enable = true;
    } else {
		btnGenerateSeat.Enable = false;
		btnViewSeat.Enable = false;
    }
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveEducationPlace(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    lfn_Cancel(); // 값 초기화
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 교육장 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdEducationPlace, 0); // 멀티 정렬을 실행한다.
}

function ds_ioEducationPlace_OnRowPosChanged(obj,nOldRow,nRow) {
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        cbxJibu.Value = obj.GetColumn(nRow, "EPMGGTMGNO");
        cbxRegion.Value = obj.GetColumn(nRow, "EPRREGIONCD");
        edEducationPlaceName.Text = obj.GetColumn(nRow, "EPNM");
        edImageFile.Text = obj.GetColumn(nRow, "EPSAVENM");
        ls_RealFileName = obj.GetColumn(nRow, "EPREALNM");
        edParkingInfo.Text = obj.GetColumn(nRow, "EPPARKINGINFO");

        gfn_MakeKeyMark(this); // 키값 수정 금지 설정

        lb_FileUpload = false;

        btnGenerateSeat.Visible = true;
	}

	mb_IsNewData = false;

}

/**
 * 파일 업로드 되었을 때
 */
function btnUploadFile_OnClick(obj) {

	gfn_ImageViewPopup(
        ds_ioEducationPlace.GetColumn(ds_ioEducationPlace.currow, "EPDIRPATH")
        , ls_RealFileName
        , edImageFile.text
        , "MAP"
    );

/*
    fdImageFile.Type = "OPEN";

    if (fdImageFile.open()) {
        edImageFile.text = fdImageFile.FileName;
        lb_FileUpload = true;
    }
*/
}

/**
 * 파일 다운로드
 */
function lfn_DownloadFile(obj) {

    if (length(ls_RealFileName) > 0) {

        fdImageFile.Type = "SAVE";
        fdImageFile.FileName = edImageFile.text + ".gif";

        if (fdImageFile.open()) {
            var arrRtn = tit_fileRead(
                hfImageFile
                , ls_FileUploadPath
                , ls_RealFileName
                , fdImageFile.FilePath
                , fdImageFile.FileName
            );

            gfn_Alert(MSGI00060); //다운로드 완료 메시지
        }
    }

}

/**
 * 빠른파일 다운로드
 */
function gdEducationPlace_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

    var lobj_Dataset = object(obj.BindDataset);

    ds_ioEducationPlace_OnRowPosChanged(lobj_Dataset,nRow,nRow);

	if(nCell == 2) {
        lfn_DownloadFile(obj);
	}
}

/**
 * 경기 북부 일 경우
 */
function cbxJibu_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {

	if (cbxJibu.value == "1141") {
       lbRegion.Visible = true;
       cbxRegion.Visible = true;
	} else {
       lbRegion.Visible = false;
       cbxRegion.Visible = false;
	}

	edEducationPlaceName.Text = "";
	edImageFile.Text = "";
	edParkingInfo.Text = "";
}

/**
 * 좌석배치생성
 */
function btnGenerateSeat_OnClick(obj)
{
	var sArg  =  "sJibucode=" + quote(ds_ioEducationPlace.GetColumn(ds_ioEducationPlace.currow, "EPMGGTMGNO"));
		sArg += " sJibuname=" + quote(ds_ioEducationPlace.GetColumn(ds_ioEducationPlace.currow, "GTDEPTNM"));
		sArg += " sEducode="  + quote(ds_ioEducationPlace.GetColumn(ds_ioEducationPlace.currow, "EPMGNO"));
		sArg += " sEduname="  + quote(ds_ioEducationPlace.GetColumn(ds_ioEducationPlace.currow, "EPNM"));

	Dialog("spcledu::frmspcledu9002PEducationSeatViewerPopup.xml", sArg, 860, 768, "", -1, -1);	//좌석배치보기 팝업을 호출한다.
}   

