Window.Script:
/*

 * 사용자정보명 : frmsys0021PUserList.xml

 * 설명 : 사용자정보검색팝업

 * 작성일 : 2009.07.10

 * 작성자 : 장성호

 */



#include "LIB::COMMON.js"; // 공통 관련



/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리



    if (IsExistVar("sUserName")) {

        edUser.Text = sUserName;

    }



    lfn_search(obj); // 사용자정보 조회

}



function Form_OnUnloadCompleted(obj) {

    Close();

}



function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    switch (nChar) {

        case 27 : // 종료(Esc)

            lfn_End();

            break;

        case 113 : // 조회(F2)

            lfn_Search();

            break;

    }

}



/*

 * 기능 : 조회

 */

function lfn_Search(obj) {

    ds_oUser.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();

	tit_addCsvSearchActionInfo("sys:searchUserList", false);

	// 서버 호출 

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_oUser=ds_oUser"	// outDs

		,"USER=" + quote(edUser.Text)// args

            + " DEPT=" + quote(iif(gfn_IsNull(gds_oUserInfo.GetColumn(0, "ADMINGUBUN")), gds_oUserInfo.GetColumn(0, "PDEPTCD"), "")) // SM, BM이면 부서 전체, 아니면 부서코드 지정

            + " SEL=N"

            + " USERGUBUN=" + quote(radUserGubun.Value)

		,"lfn_AfterSearchUser"

		,false

	);

}



/*

 * 기능 : 종료

 */

function lfn_End(obj) {

    Close();

}

/* 공통 필수 종료 */

/* 사용자 함수 */

/*

 * 기능 : 공통코드 조회 후 처리해야 할 내용

 * parameter : nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchUser(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }



    gfn_SearchMessage(gdUser); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

}



/* 사용자 이벤트 함수 */

function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {

    var lobj_Dataset = object(obj.BindDataset);

    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.

    if (lobj_Dataset.GetRowCount() > 1) {

        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.

    }

}



function btnMutilSort_OnClick(obj)

{

	gfn_GridMultiSort(gdUser, 0); // 멀티 정렬을 실행한다.

}



function btnSelect_OnClick(obj)

{

    var arrArg = Array(6);

    arrArg[0] = ds_oUser.GetColumn(ds_oUser.currow, "PDEPTCD");

    arrArg[1] = ds_oUser.GetColumn(ds_oUser.currow, "PDEPTNM");

    arrArg[2] = ds_oUser.GetColumn(ds_oUser.currow, "PPOSITNCD");

    arrArg[3] = ds_oUser.GetColumn(ds_oUser.currow, "PPOSITNNM");

    arrArg[4] = ds_oUser.GetColumn(ds_oUser.currow, "PSABUN");

    arrArg[5] = ds_oUser.GetColumn(ds_oUser.currow, "PPERSONNM");

    Close(arrArg);

}



function gdUser_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)

{

    btnSelect_OnClick(obj);

}



function edUser_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)

{

	if (nChar == 13) {

        lfn_Search();

	}

}


function radUserGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_Search();
}
