#include "LIB::sys.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    if (gfn_IsNull(gds_oUserInfo.GetColumn(0, "ADMINGUBUN"))) { // SM, BM이 아니면
        lbDept.Visible = false;
        edDept.Visible = false;
        edDept.UserData = "";
    }

	// 주무부서에서 사용자 권한관리
	if (gds_oUserInfo.GetColumn(0,"ADMINGUBUN") != "SM")  // 시스템관리자가 아니면
		lfn_SearchDeptAdminAuth();    
		
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdUserAuth.Height = nCy - gdUserAuth.Top - 4;
        gdUser.Height = nCy - gdUser.Top - 4;
    }
}

// 주무부서에서 사용자 권한관리 위해 주무부서별 메뉴권한 조회
function lfn_SearchDeptAdminAuth(obj) {
var DEPTCD = "";

    ds_ioDeptAuth.ClearData(); // 데이타셋 초기화

	DEPTCD = gds_oUserInfo.GetColumn(0, "PDEPTCD");
	
	tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchDeptAdminAuth", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioDeptAuth=ds_oDeptAuth"	// outDs
		,"DEPTCD=" + quote(DEPTCD)	// args 
		,""
		,false
	);
}

/*
 * 기능 : 복사할 사용자 권한 및 복사받을 사용자 조회
 */
function lfn_Search(obj) {
    lfn_SearchUserAuth(); // 복사할 사용자 권한 조회
    lfn_SearchUser(); // 복사받을 사용자 조회
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	if (!gfn_CheckChangedGridTreeview(this)) {
        gfn_Alert("복사받을 사용자를 선택해 주십시오.");
        return false;
    }

	if (!gfn_Confirm("정말로 권한 복사 하시겠습니까?")) {
        return false;
	}

    tit_clearActionInfo();
	tit_addMultiActionInfo("sys:copyUserAuth", "", 0, "", "N");
	//서버 호출 
	tit_callService(
		""
		,""
		,"ds_iUser=ds_ioUser:U"	// inDs
		,""	// outDs
		,"ORGPSABUN=" + quote(edUserId.Text)
			+ " ORGPDEPTCD=" + quote(ls_OrgAuthDeptCd) // args 
			
			+ " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))	// args 
		,"lfn_AfterSaveUserAuth"
		,false
	);
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 권한 조회
 */
function lfn_SearchUserAuth(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

    ds_oUserAuth.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchUserAuth", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oUserAuth=ds_oUserAuth"	// outDs
		,"AUTHSABUN=" + quote(edUserId.Text)
            + " AUTHDEPTCD=" + quote(ls_OrgAuthDeptCd)		
            + " MMENUID=" + quote("0000000001") // 루트
            
            + " DAAGUBUN=" + quote(ds_ioDeptAuth.GetColumn(0, "DAAGUBUN"))
		,"lfn_AfterSearchUserAuth"
		,false
	);
}

/*
 * 기능 : 권한 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchUserAuth(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdUserAuth); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : 사용자정보 조회
 */
function lfn_SearchUser(obj) {
    // 전체 선택 초기화
    gdUser.SetCellProp("head", 0, "text", 0); // 사용유무

    ds_ioUser.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchUserList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioUser=ds_oUser"	// outDs
		,"DEPT=" + quote(iif(gfn_IsNull(gds_oUserInfo.GetColumn(0, "ADMINGUBUN")), gds_oUserInfo.GetColumn(0, "PDEPTCD"), edDept.Text)) // SM, BM이면 선택 부서, 아니면 사용자 부서
         	+ " USER=" + quote(edUser.Text)
         	
         	+ " USERGUBUN=" + quote(iif(ls_OrgAuthDeptCd == "0000", "1", "0"))	
         	+ " SEL='N'"   // args
		,"lfn_AfterSearchUser"
		,false
	);
}

/*
 * 기능 : 사용자정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchUser(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdUser); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveUserAuth(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnSave.Enable = bStatus; // 수정&저장 버튼

    gdUser.Editable = bStatus;
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdUser, 1); // 멀티 정렬을 실행한다.
}

function edUser_OnFocus(obj)
{
    btnSearchUser.SetFocus();
}

var ls_OrgAuthDeptCd = "";
function btnSearchUser_OnClick(obj)
{
    var arrUser = gfn_UserListPopup();
    if (length(arrUser) > 0) {
        ls_OrgAuthDeptCd = arrUser[0];
        edDeptName.Text = arrUser[1];
        edPositnCopyOrg.Text = arrUser[3];
        edUserId.Text = arrUser[4];
        edUserName.Text = arrUser[5];
        lfn_SearchUserAuth();
    }
}

function edUser_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        lfn_searchUser(); // 사용자정보 조회
	}
}

function gdUser_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	if (nCell == 0) { // 선택
        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우
            obj.SetCellProp("head", nCell, "text", 0);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "0");
            }
        } else { // 전체가 이미 선택되지 않은 경우
            obj.SetCellProp("head", nCell, "text", 1);
            for(i = 0; i < lobj_Dataset.RowCount(); i++) {
                lobj_Dataset.SetColumn(i, "SEL", "1");
            }
        }
	} else { // 헤드 정렬
        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);
	}
}
