#include "LIB::sys.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oCourseGubun, "TCOURSEGUBUN", "Y", "전체");
    cbxCourseGubun.Value = "";
    gfn_SearchCommonCode(ds_oMunjeGubun, "MUNJEGUBUN", "Y", "전체");
    cbxMunjeGubun.Value = "";
    gfn_SearchCommonCode(ds_oSearchGubun, "TCSEARCHGUBUN", "Y", "전체");
    cbxSearchGubun.Value = "";
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdCourseCode.Height = nCy - gdCourseCode.Top - 4;
        gdCourseCode.Width = nCx - gdCourseCode.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioCourseCode.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "직능코드=" + quote(edCourseCode.Text)
                + ",직능명=" + quote(edCourseCodeName.Text)
                + ",과정=" + quote(cbxCourseGubun.Text)
                + ",직능약칭=" + quote(edCourseNickName.Text)
                + ",조회구분=" + quote(cbxSearchGubun.Text)
                + ",면제구분=" + quote(cbxMunjeGubun.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchTrainingCourseCode", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioCourseCode=ds_oCourseCode"	// outDs
		,"TCCOURSECD=" + quote(edCourseCode.Text)
            + " TCCOURSENM=" + quote(edCourseCodeName.Text)
            + " TCCOURSEGUBUN=" + quote(cbxCourseGubun.Value)
            + " TCCOURSENICK=" + quote(edCourseNickName.Text)
            + " TCSEARCHGUBUN=" + quote(cbxSearchGubun.Value)
            + " TCMUNJEGUBUN=" + quote(cbxMunjeGubun.Value)	// args 
		,"lfn_AfterSearchCourseCode"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
    lfn_Cancel(); // 값 초기화

    // 기본값 설정
    chkUseYn.Value = "Y";
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_ioCourseCode.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}

    ds_ioCourseCode.DeleteRow(ds_ioCourseCode.currow); // 선택된 ROW를 삭제한다.

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "sys:deleteTrainingCourseCode"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iCourseCode=ds_ioCourseCode:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveCourseCode"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	if (mb_IsNewData) { // 신규 저장
        // 키 중복 체크
    
        if (!gfn_Confirm(MSGW00020) || ds_ioCourseCode.GetCount() == 0) { // 정말로 추가 저장 하시겠습니까?
			return false;
		}
	
		// 입력된 값을 그리드로 복사한다.
		ds_ioCourseCode.InsertRow(0);
		ds_ioCourseCode.SetColumn(0, "TCCOURSECD", edCourseCode.Text);
		ds_ioCourseCode.SetColumn(0, "TCCOURSENM", edCourseCodeName.Text);
		ds_ioCourseCode.SetColumn(0, "TCCOURSEGUBUN", cbxCourseGubun.Value);
		ds_ioCourseCode.SetColumn(0, "TCMUNJEGUBUN", cbxMunjeGubun.Value);
		ds_ioCourseCode.SetColumn(0, "TCSEARCHGUBUN", cbxSearchGubun.Value);
		ds_ioCourseCode.SetColumn(0, "USE_YN", chkUseYn.Value);
		ds_ioCourseCode.SetColumn(0, "TCORDERSEQ", medOrderSeq.Value);
		ds_ioCourseCode.SetColumn(0, "TCCOURSENICK", edCourseNickName.Text);
		ds_ioCourseCode.SetColumn(0, "TCREQUIREDDOCS", edDocs.Text);
		ds_ioCourseCode.SetColumn(0, "TCTERMTIME", edCourseTermTime.Text);
    } else { // 수정 저장
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
			return false;
		}
	
		// 입력된 값을 그리드로 복사한다.
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCCOURSECD", edCourseCode.Text);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCCOURSENM", edCourseCodeName.Text);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCCOURSEGUBUN", cbxCourseGubun.Value);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCMUNJEGUBUN", cbxMunjeGubun.Value);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCSEARCHGUBUN", cbxSearchGubun.Value);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "USE_YN", chkUseYn.Value);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCORDERSEQ", medOrderSeq.Value);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCCOURSENICK", edCourseNickName.Text);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCREQUIREDDOCS", edDocs.Text);
		ds_ioCourseCode.SetColumn(ds_ioCourseCode.currow, "TCTERMTIME", edCourseTermTime.Text);
    }

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		"sys:insertTrainingCourseCode"	// insert
		, "sys:updateTrainingCourseCode"	// update
		, ""	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iCourseCode=ds_ioCourseCode:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveCourseCode"
		,false
	);
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 직능코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchCourseCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdCourseCode); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveCourseCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 직능코드 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdCourseCode, 0); // 멀티 정렬을 실행한다.
}

function ds_ioCourseCode_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        edCourseCode.Text = obj.GetColumn(nRow, "TCCOURSECD");
        edCourseCodeName.Text = obj.GetColumn(nRow, "TCCOURSENM");
        cbxCourseGubun.Value = obj.GetColumn(nRow, "TCCOURSEGUBUN");
        cbxMunjeGubun.Value = obj.GetColumn(nRow, "TCMUNJEGUBUN");
        cbxSearchGubun.Value = obj.GetColumn(nRow, "TCSEARCHGUBUN");
        chkUseYn.Value = obj.GetColumn(nRow, "USE_YN");
        medOrderSeq.Value = obj.GetColumn(nRow, "TCORDERSEQ");
        edCourseNickName.Value = obj.GetColumn(nRow, "TCCOURSENICK");
        edDocs.Value = obj.GetColumn(nRow, "TCREQUIREDDOCS");
        edCourseTermTime.Value = obj.GetColumn(nRow, "TCTERMTIME");
        
        // 키값 수정 금지
        gfn_MakeKeyMark(this); // 키값 수정 금지 설정
	}
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

function gdCourseCode_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	ds_ioCourseCode_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}
