Window.Script:
/*
 * 우편번호정보명 : frmsys0051PZipCodeList.xml
 * 설명 : 우편번호정보검색팝업
 * 작성일 : 2009.07.16
 * 작성자 : 이규희
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sDongName")) {
        edDong.Text = sDongName;
        if (!gfn_IsNull(sDongName)) {
            lfn_Search();
        }
    }
    if (IsExistVar("sZipcode")) {
        edZipCode.Text = sZipcode;
        if (!gfn_IsNull(sZipcode)) {
            lfn_Search();
        }
    }
    edDong.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (gfn_IsNull(edZipCode.Text) && gfn_IsNull(edDong.Text)) {
        gfn_Alert("최소 하나의 검색조건을 입력하십시오."); 
        edZipCode.Text = ""; // 초기화
        edDong.Text = ""; // 초기화
        edDong.SetFocus();
        return false;
    } else {
        ds_oZipCode.ClearData(); // 데이타셋 초기화
        
        // 조회이력을 저장한다.  
        ls_HistoryArg = "우편번호=" + quote(edZipCode.Text)	// args 
                + ",동=" + quote(edDong.Text);
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , "우편번호검색팝업"
            , ls_HistoryArg
        );
    
        tit_clearActionInfo();
        tit_addSearchActionInfo("sys:searchZipCodeList", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oZipCode=ds_oZipCode"	// outDs
            ,"ZZIPCD=" + quote(edZipCode.Text)	// args 
                + " ZDONG=" + quote(edDong.Text)
            ,"lfn_AfterSearchZipCode"
            ,false
        );
	}
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */
/* 우편번호 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchZipCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oZipCode.GetCount() > 0) {
        gdZipCode.SetFocus();
    } else {
        gdZipCode.SetFocus();
    }

    gfn_SearchMessage(gdZipCode); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 우편번호 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdZipCode, 0); // 멀티 정렬을 실행한다.
}

function btnSelect_OnClick(obj)
{
    var arrArg = Array(6);
    arrArg[0] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "ZZIPCD");
    arrArg[1] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "SIKUDONG");
    arrArg[2] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "ZSEQ");
    Close(arrArg);
}

function gdZipCode_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    btnSelect_OnClick(obj);
}

function edZipCode_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        lfn_Search();
	}
}

function gdZipCode_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}
