#include "LIB::sys.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdZipCode.Height = nCy - gdZipCode.Top - 4;
        //gdZipCode.Width = nCx - gdZipCode.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    if (gfn_IsNull(medZipCode.Value) && gfn_IsNull(edSido.Text) && gfn_IsNull(edKu.Text) && gfn_IsNull(edDong.Text)) {
        gfn_Alert("우편번호/시도/시군구/읍면동 중에 \n최소 하나의 검색조건을 입력하십시오.");
        medZipCode.SetFocus();
        return;
    }

    ds_ioZipCode.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("sys:searchNewZipCode", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioZipCode=ds_oZipCode"	// outDs
		,"NZZIPCDE=" + quote(medZipCode.Value)
		    + " NZSIDO=" + quote(edSido.Text)
		    + " NZKU=" + quote(edKu.Text)
		    + " NZDONG=" + quote(edDong.Text)		    
		    + " NZRI=" + quote(edRi.Text)
		    + " NZSAN=" + quote(edSan.Text)
		    + " NZROADNM=" + quote(edRoadNm.Text)
		    + " NZJIBUNBON=" + quote(edJibunBun.Text)
		    + " NZJIBUNBU=" + quote(edJibunBu.Text)
		    + " NZNEWBONBEN=" + quote(edNewbunBun.Text)
		    + " NZNEWBUBEN=" + quote(edNewbunBu.Text)
		    + " NZBULDNO=" + quote(edBuldNo.Text)
		    + " NZBULDNM=" + quote(edBuldNm.Text)
		    + " NZSANGSEBULDNM=" + quote(edSangseBuldNm.Text)
		    + " NZLEGALCD=" + quote(edLegalCd.Text)
		,"lfn_AfterSearchZipCode"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
	medZipCode.SetFocus();
    lfn_Cancel(); // 값 초기화

    // 기본값 설정
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_ioZipCode.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}

    ds_ioZipCode.DeleteRow(ds_ioZipCode.currow); // 선택된 ROW를 삭제한다.

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "sys:deleteNewZipCode"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iZipCode=ds_ioZipCode:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveZipCode"
		,false
	);
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

	if (mb_IsNewData) { // 신규 저장
        // 키 중복 체크

        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
			return false;
		}

		// 입력된 값을 그리드로 복사한다.
		ds_ioZipCode.InsertRow(0);
		ds_ioZipCode.SetColumn(0, "NZZIPCDE", medZipCode.Value);
		ds_ioZipCode.SetColumn(0, "NZLEGALCD", edLegalCd.Text);
		ds_ioZipCode.SetColumn(0, "NZSIDO", edSido.Text);
		ds_ioZipCode.SetColumn(0, "NZKU", edKu.Text);
		ds_ioZipCode.SetColumn(0, "NZDONG", edDong.Text);
		ds_ioZipCode.SetColumn(0, "NZRI", edRi.Text);
		ds_ioZipCode.SetColumn(0, "NZSAN", edSan.Text);
		ds_ioZipCode.SetColumn(0, "NZJIBUNBON", edJibunBun.Text);
		ds_ioZipCode.SetColumn(0, "NZJIBUNBU", edJibunBu.Text);
		ds_ioZipCode.SetColumn(0, "NZROADNM", edRoadNm.Text);
		ds_ioZipCode.SetColumn(0, "NZNEWBONBEN", edNewbunBun.Text);
		ds_ioZipCode.SetColumn(0, "NZNEWBUBEN", edNewbunBu.Text);
		ds_ioZipCode.SetColumn(0, "NZBULDNO", edBuldNo.Text);
		ds_ioZipCode.SetColumn(0, "NZBULDNM", edBuldNm.Text);
		ds_ioZipCode.SetColumn(0, "NZSANGSEBULDNM", edSangseBuldNm.Text);
    } else { // 수정 저장
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
			return false;
		}

		// 입력된 값을 그리드로 복사한다.
		ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZZIPCDE", medZipCode.Value);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZLEGALCD", edLegalCd.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZSIDO", edSido.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZKU", edKu.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZDONG", edDong.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZRI", edRi.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZSAN", edSan.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZJIBUNBON", edJibunBun.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZJIBUNBU", edJibunBu.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZROADNM", edRoadNm.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZNEWBONBEN", edNewbunBun.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZNEWBUBEN", edNewbunBu.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZBULDNO", edBuldNo.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZBULDNM", edBuldNm.Text);
        ds_ioZipCode.SetColumn(ds_ioZipCode.currow, "NZSANGSEBULDNM", edSangseBuldNm.Text);
    }

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		"sys:insertNewZipCode"	// insert
		, "sys:updateNewZipCode"	// update
		, ""	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iZipCode=ds_ioZipCode:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSaveZipCode"
		,false
	);
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 우편번호 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchZipCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdZipCode); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveZipCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    //lfn_Search(); // 우편번호 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdZipCode, 0); // 멀티 정렬을 실행한다.
}

function ds_ioZipCode_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {

        medSeq.Value = obj.GetColumn(nRow, "NZMGNO");
        medZipCode.Value = obj.GetColumn(nRow, "NZZIPCDE");
        edLegalCd.Text = obj.GetColumn(nRow, "NZLEGALCD");
        edSido.Text = obj.GetColumn(nRow, "NZSIDO");
        edKu.Text = obj.GetColumn(nRow, "NZKU");
        edDong.Text = obj.GetColumn(nRow, "NZDONG");
        edRi.Value = obj.GetColumn(nRow, "NZRI");
        edSan.Text = obj.GetColumn(nRow, "NZSAN");
        edJibunBun.Text = obj.GetColumn(nRow, "NZJIBUNBON");
        edJibunBu.Text = obj.GetColumn(nRow, "NZJIBUNBU");
        edRoadNm.Text = obj.GetColumn(nRow, "NZROADNM");
        edNewbunBun.Text = obj.GetColumn(nRow, "NZNEWBONBEN");
        edNewbunBu.Text = obj.GetColumn(nRow, "NZNEWBUBEN");
        edBuldNo.Text = obj.GetColumn(nRow, "NZBULDNO");
        edBuldNm.Text = obj.GetColumn(nRow, "NZBULDNM");
        edSangseBuldNm.Text = obj.GetColumn(nRow, "NZSANGSEBULDNM");

        // 키값 수정 금지
        gfn_MakeKeyMark(this); // 키값 수정 금지 설정
	}
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

function gdZipCode_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	ds_ioZipCode_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}
