Window.Script:
/*
 * 우편번호정보명 : frmsys0053PNewZipCodeList.xml
 * 설명 : 신주소우편번호정보검색팝업
 * 작성일 : 2010.02.11
 * 작성자 : 이규희
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    /*
    if (IsExistVar("sDongName")) {
        edDong.Text = sDongName;
        if (!gfn_IsNull(sDongName)) {
            lfn_Search();
        }
    }
    */
    ds_oKu.ClearData();
    ds_oKu.InsertRow(0);
    ds_oKu.SetColumn(0, "DATA", "선택");
    cbxKu.Index = 0;

    if (IsExistVar("sZipcode")) {
        medZipCode.Value = sZipcode;
        if (!gfn_IsNull(sZipcode)) {
            lfn_Search();
        }
    }

    cbxSido.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    if (!gfn_IsNull(medZipCode.Value) && length(medZipCode.Value) >= 3) {
        ds_oZipCode.ClearData(); // 데이타셋 초기화

        // 조회이력을 저장한다.  
        ls_HistoryArg = "우편번호=" + quote(medZipCode.Value);	// args 
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , "신주소우편번호검색팝업"
            , ls_HistoryArg
        );

        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("sys:searchNewZipCodeList", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oZipCode=ds_oZipCode"	// outDs
            ,"NZZIPCDE=" + quote(Replace(iif(gfn_IsNull(medZipCode.Value), medZipCode.Value, medZipCode.Text), "_", ""))	// args 
                + " NZSIDO=" + quote(cbxSido.Value)
                + " NZKU=" + quote(cbxKu.Value)
                //+ " NZDONG=" + quote(edDong.Text)
                + " NZROADNM=" + quote(edRoadNm.Text)
            ,"lfn_AfterSearchZipCode"
            ,false
        );
    } else if (cbxSido.Text == "선택" || cbxKu.Text == "선택") {
        gfn_Alert("'시도'와 '시군구'는 필수입력사항입니다 ."); 
        cbxSido.SetFocus();
        return false;
    } else {
        ds_oZipCode.ClearData(); // 데이타셋 초기화

        // 조회이력을 저장한다.  
        ls_HistoryArg = "우편번호=" + quote(medZipCode.Value);	// args                 
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , "신주소우편번호검색팝업"
            , ls_HistoryArg
        );

        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("sys:searchNewZipCodeList", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oZipCode=ds_oZipCode"	// outDs
            ,"NZZIPCDE=" + quote(Replace(iif(gfn_IsNull(medZipCode.Value), medZipCode.Value, medZipCode.Text), "_", ""))	// args 
                + " NZSIDO=" + quote(cbxSido.Text)
                + " NZKU=" + quote(cbxKu.Text)
                //+ " NZDONG=" + quote(edDong.Text)
                + " NZROADNM=" + quote(edRoadNm.Text)
            ,"lfn_AfterSearchZipCode"
            ,false
        );
    }
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */
/* 우편번호 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchZipCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oZipCode.SaveXML());
    if (ds_oZipCode.GetCount() > 0) {
        gdZipCode.SetFocus();
    } else {
        gdZipCode.SetFocus();
    }

    gfn_SearchMessage(gdZipCode); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 우편번호 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdZipCode, 0); // 멀티 정렬을 실행한다.
}

function btnSelect_OnClick(obj)
{
    var sNewAddr = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZSIDO") + 
                   " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZKU") +
                   " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZDONG") +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZRI")), "" , " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZRI")) +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZSAN")), "" , " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZSAN")) +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBONBEN")), "" , " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBONBEN")) +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBUBEN")), "" , "-" + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBUBEN")) +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZROADNM")), "" , " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZROADNM")) +
                   iif(gfn_IsNull(ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZBULDNM")), "" , " " + ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZBULDNM"));                   

    var arrArg = Array(10);
    arrArg[0] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZZIPCDE");
    arrArg[1] = sNewAddr;
    arrArg[2] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZMGNO");
    arrArg[3] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZROADNM");
    arrArg[4] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZBULDNM");

    arrArg[5] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBONBEN");
    arrArg[6] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZNEWBUBEN");
    arrArg[7] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZSANGSEBULDNM");
    arrArg[8] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZLEGALCD");
    arrArg[9] = ds_oZipCode.GetColumn(ds_oZipCode.currow, "NZBULDNO");
    Close(arrArg);
}

function gdZipCode_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    btnSelect_OnClick(obj);
}

function edZipCode_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        lfn_Search();
	}
}

function gdZipCode_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}

function cbxSido_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (gfn_IsNull(cbxSido.Value)) {
        ds_oKu.ClearData();
        ds_oKu.InsertRow(0);
        ds_oKu.SetColumn(0, "DATA", "선택");
        cbxKu.Index = 0;
	} else {
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("sys:searchNewZipCodeKu", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oKu=ds_oZipCode"	// outDs
            ,"NZSIDO=" + quote(cbxSido.Text)	// args 
            ,""
            ,false
            ,false
            ,true
        );
	}
}
