#include "LIB::sys.js"; // 공통업무 관련

var lb_FileUpload = false;
var ls_FileUploadPath = "/BBSDATA";
var ls_BoardGubun = "U";
var ls_RealFileName = "";
var ls_UploadFileName = "UPDATE"; //서버에 저장될 파일 명

////////////////////
/* 공통 필수 시작 */
////////////////////

function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    lfn_Search();
    edTitle.SetFocus();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdBoard.Height = nCy - gdBoard.Top - 4;
    }
}

/**
 * 엑셀 출력
 */
function lfn_Excel(obj) {
    if (ds_ioBoard.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }
}

/**
 * 기능 : 초기화
 */
function lfn_Cancel(obj) {
   gfn_FormInit(this);
   ls_RealFileName = "";
   lbFileSize.Text = "";
   lfn_FileDownCheck();
}

/**
 * 기능 : 조회
 */
function lfn_Search(obj) {

    ds_ioBoard.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("sys:searchBoard", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioBoard=ds_oBoard"	// outDs
		,"BBOARDGUBUN=" + quote(ls_BoardGubun)
            + " BREGSABUN=" + quote(edWriter.Text)
            + " BTITLE=" + quote(edTitle.Text)	// args 
		,"lfn_AfterSearchBoard"
		,false
	);
}

/**
 * 기능 : 추가
 */
function lfn_Input(obj) {

	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}
	
    lfn_Cancel();
    mb_IsNewData = true;
    edCreateDate.Value = today();
    edWriter.Text = gds_oUserInfo.GetColumn(0,"PPERSONNM");
    edWriter.Readonly = true;
    edWriter.BKColor = "user4";
    
    edTitle.SetFocus();

}

/**
 * 기능 : 저장
 */
function lfn_Save(obj) {

    var ls_FileUploadService = ""; //파일 업로드일 경우 사용되는 변수
    var nRow = 0; //추가인지 수정인지 구분
    var ls_UploadRealName = "";

	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}

    if (mb_IsNewData) { //추가
        if (!gfn_Confirm(MSGW00020)) { // 정말로 추가 저장 하시겠습니까?
            return false;
        }
        ds_ioBoard.InsertRow(0);
        
    } else { //수정
    
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 하시겠습니까?
            return false;
        }
        nRow = ds_ioBoard.currow;
        
    }

    ds_ioBoard.SetColumn(nRow, "BBOARDGUBUN", ls_BoardGubun);
    ds_ioBoard.SetColumn(nRow, "BTITLE", edTitle.Text);
    ds_ioBoard.SetColumn(nRow, "BCONTENTS", taContents.Text);
    
    //게시글 등록 액션
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		"sys:insertBoard" // insert
		, "sys:updateBoard" // update
		, "" // delete
		, "" // flag column 명
		, "" // key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N" // 실행타입
	);
	
    if(lb_FileUpload) { //파일 업로드일 경우

        ls_UploadRealName = ls_UploadFileName + "_" + getDate() + right(timem(),3) + gfn_GetFileExtensionName(fdAttachFile.FileName);

        ds_ioBoard.SetColumn(nRow, "AFSAVENM", fdAttachFile.FileName);
        ds_ioBoard.SetColumn(nRow, "AFREALNM", ls_UploadRealName);
        ds_ioBoard.SetColumn(nRow, "AFFILESIZE", StripNum(lbFileSize.text));
        ds_ioBoard.SetColumn(nRow, "AFDIRPATH", ls_FileUploadPath);
        
        tit_addSaveActionInfo(
            "sys:insertAttachFile" // insert
            , "sys:insertAttachFile" // update
            , "" // delete
            , "" // flag column 명
            , "" // key sql id
            , 0	// key 증가 값
            , "" 
            , 0 
            , "N" // 실행타입
        );
        
        ls_FileUploadService = " ds_iAttachFile=ds_ioBoard:U";

    }
	
    gfn_FormInit(this); // 입력란 초기화

	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iBoard=ds_ioBoard:U" + ls_FileUploadService	// inDs
		,""	// outDs
		,""	// args
		,"lfn_AfterSaveBoard"
		,false
	);

    if (lb_FileUpload) { //파일 변경일 경우

        if (Length(fdAttachFile.FileName) < 1) { //삭제 일 경우

            tit_FileDelete(
                ls_FileUploadPath
                , ls_RealFileName
                , false
            );

        } else { //변경일 경우

            tit_FileWrite(
                hfAttachFile
                , ls_FileUploadPath
                , fdAttachFile.FilePath
                , fdAttachFile.FileName
                , ls_UploadRealName
                , "U"
                , ls_RealFileName
            );
            
            hfAttachFile.CloseFile();

        }
    }

    mb_IsNewData = false;
}

/**
 * 기능 : 삭제
 */
function lfn_Delete(obj) {

    var ls_FileUploadService = ""; //파일 업로드일 경우 사용되는 변수
    
    if (ds_ioBoard.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}
	
    ds_ioBoard.DeleteRow(ds_ioBoard.currow); // 선택된 ROW를 삭제한다.
    gfn_FormInit(this); // 입력란 초기화
    
	tit_clearActionInfo();

    if (length(ls_RealFileName) >= 1) { //첨부 파일이 존재할 경우

        tit_addSaveActionInfo(
            ""	// insert
            , ""	// update
            , "sys:deleteAttachFile" // delete
            , ""	// flag column 명
            , ""	// key sql id
            , 0	// key 증가 값
            , "" 
            , 0 
            , "N"	// 실행타입
        );
        
        ls_FileUploadService = "ds_iAttachFile=ds_ioBoard:A ";

    }
    
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "sys:deleteBoard" // delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);

	// 서버 호출
	tit_callService(
		""
		,""
		,ls_FileUploadService + "ds_iBoard=ds_ioBoard:A"	// inDs
		,""	// outDs
		,"" // args 
		,"lfn_AfterSaveBoard"
		,false
	);

    if (length(ls_RealFileName) >= 1) { //첨부 파일이 존재할 경우

        tit_FileDelete( //파일 삭제
            ls_FileUploadPath
            , ls_RealFileName
            , false
        );
        
        ls_RealFileName = "";
    }
    
}


