/**
 * 버튼 정보 변경
 */
function lfn_FileDownCheck(){

    if (length(ls_RealFileName) > 0 && !lb_FileUpload) {
        btnUploadFile.Text = "삭제";
    } else {
        btnUploadFile.Text = "업로드";
    }
}

