/**
 * 멀티 정렬
 */
function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdBoard, 0); // 멀티 정렬을 실행한다.
}

/**
 * 조회 후 처리
 */
function lfn_AfterSearchBoard(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdBoard); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/**
 * 저장 후 처리
 */
function lfn_AfterSaveBoard(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    lb_FileUpload = false; //업로드 구분을 해지시킨다.
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 공통코드 조회
}

/**
 * 그리드의 값을 입력란에 복사
 */
function ds_ioBoard_OnRowPosChanged(obj,nOldRow,nRow) {

    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        edCreateDate.Value = obj.GetColumn(nRow, "BREGDATE");
        edWriter.Text = obj.GetColumn(nRow, "BREGSABUN");
        edTitle.text = obj.GetColumn(nRow, "BTITLE");
        taContents.text = replace(obj.GetColumn(nRow, "BCONTENTS"),"|r|n",chr(13)+chr(10));
        edAttachFile.Text = obj.GetColumn(nRow, "AFSAVENM");
        ls_RealFileName = obj.GetColumn(nRow, "AFREALNM");
        
        if(length(ls_RealFileName) >= 1) {
            lbFileSize.text = NumFormat(obj.GetColumn(nRow, "AFFILESIZE")) + " Byte";
        } else {
            lbFileSize.text = "";
        }
        
        lb_FileUpload = false;
	}
	
	lfn_FileDownCheck();
    mb_IsNewData = false;
}

/**
 * 파일 업로드 되었을 때
 */
function btnUploadFile_OnClick(obj) {

    if (btnUploadFile.Text == "삭제") {
    
        lbFileSize.text = "";
        edAttachFile.text = "";
        btnUploadFile.Text = "업로드";
        lb_FileUpload = true;

    } else {
    
        fdAttachFile.Type = "OPEN";
        
        if (fdAttachFile.open()) {
            fiAttachFile.FileName = fdAttachFile.FilePath + "\\" + fdAttachFile.FileName;
            
            edAttachFile.text = fdAttachFile.FileName;
            lbFileSize.text = NumFormat(fiAttachFile.GetLength()) + " Byte";
            
            fiAttachFile.Close();

            lb_FileUpload = true;
        }
    }

}

/**
 * 파일 다운로드
 */
function lfn_DownloadFile(obj) {

    if (length(ls_RealFileName) > 0) {

        fdAttachFile.Type = "SAVE";
        fdAttachFile.FileName = edAttachFile.text;

        if (fdAttachFile.open()) {
            var arrRtn = tit_fileRead(
                hfAttachFile
                , ls_FileUploadPath
                , ls_RealFileName
                , fdAttachFile.FilePath
                , fdAttachFile.FileName
            );
            
            gfn_Alert(MSGI00060); //다운로드 완료 메시지
        }
    }
	
}

/**
 * 빠른파일 다운로드
 */
function gdBoard_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex) {

    var lobj_Dataset = object(obj.BindDataset);
 
    ds_ioBoard_OnRowPosChanged(lobj_Dataset,nRow,nRow);
 
	if(nCell == 3) {
        lfn_DownloadFile(obj);
	}
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}
