/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    // 시스템권한(SM)인지 검사한다. (SM전용 프로그램임)
    if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") <> "SM") {
        gfn_Alert(MSGI00080); // "사용 권한이 없습니다."
        close();
    }

    ms_FormAuth = gfn_GetFormAuth(this); // 사용자정보을 조회한다.
    if (ms_FormAuth == "W") { // 사용자정보에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 사용자정보
    } else {
        lfn_SetFormAuth(false); // 읽기 사용자정보
    }

    calDateStart.Value = AddDate(GetDate(), -7);
    calDateEnd.Value = GetDate();
    
    // 사용자정보 조회 
    lfn_UserSearch(gds_oUserInfo.GetColumn(0, "PDEPTCD"));
    // 부서정보 조회 
    lfn_DeptSearch();
    
    cbxDeptList.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");    
    cbxUserList.Value = gds_oUserInfo.GetColumn(0, "PSABUN");
    
    if(gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM" || gds_oUserInfo.GetColumn(0, "DEPTADMINGUBUN") == "CM") {
		cbxDeptList.Enable = true;
    } else {
		cbxDeptList.Enable = false;
    }
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdSearchHistory.Height = nCy - gdSearchHistory.Top - 4;
        gdSearchHistory.Width = nCx - gdSearchHistory.Left - 4;
    }
}

/*
 * 기능 : 부서정보 조회
 */
function lfn_DeptSearch(obj) {

    ds_ioDeptList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:search0093DeptList", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioDeptList=ds_ioDeptList"	// outDs
		,""
		,"lfn_AfterDeptSearch"
		,false
	);
}

/*
 * 기능 : 부서정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterDeptSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

/*
 * 기능 : 사용자정보 조회
 */
function lfn_UserSearch(DEPT) {

    ds_ioUserList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:search0093UserList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioUserList=ds_ioUserList"	// outDs
		,"DEPT=" + DEPT
		,"lfn_AfterUserSearch"
		,false
		,false
		,true
	);
}

/*
 * 기능 : 사용자정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterUserSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }  
	// 사용자정보 '전체'
	ds_ioUserList.InsertRow(0);
	ds_ioUserList.SetColumn(0,"NMPOSITION","전체");
}

/*
 * 기능 : 이력 조회
 */
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }

    ds_oSearchHistory.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchSearchHistory", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oSearchHistory=ds_oSearchHistory"	// outDs
		,"SHPROCSABUN=" + quote(cbxUserList.Value)// args
			+ " SHDEPT=" + quote(cbxDeptList.Value)
            + " SHGUBUN=" + quote(cbxGubun.Value)
            + " SHPROCDATESTART=" + quote(left(calDateStart.Value, 8))
            + " SHPROCDATEEND=" + quote(left(calDateEnd.Value))
            + " SHWORKGUBUNNM=" + quote(edWorkGubunNm.Text)
		,"lfn_AfterSearchSearchHistory"
		,false
	);
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 사용자정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchSearchHistory(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdSearchHistory); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

