{
    btnSearchUser.SetFocus();
}

var ls_AuthDeptCd = "";
function btnSearchUser_OnClick(obj)
{
    var arrUser = gfn_UserListPopup();
    if (length(arrUser) > 0) {
        ls_AuthDeptCd = arrUser[0];
        edDeptName.Text = arrUser[1];
        edPositn.Text = arrUser[3];
        edUserId.Text = arrUser[4];
        edUserName.Text = arrUser[5];
    }
}

/*
 * 기능 : 부서변경시 해당 부서인원 조회
*/
function cbxDeptList_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_UserSearch(strCode);
}


/*
 * 업무명 목록 팝업
*/
function btnSearchWork_OnClick(obj)
{
	var parValue = "sGubun=1";
		parValue += " sDept="+quote(cbxDeptList.Value);
		parValue += " sSabun="+quote(cbxUserList.Value);
		parValue += " sPrtGubun="+quote(cbxGubun.Value);
		parValue += " sSdate="+quote(left(calDateStart.Value, 8));
		parValue += " sEdate="+quote(left(calDateEnd.Value));
		alert (parValue);
		
	var Result = Dialog("sys::frmsys0094MHistoryWorkGubun.xml", parValue,"", "", "", -1, -1);
	
	if (Result != 0) {
		edWorkGubunNm.Text = Result;
	}
}
