#include "LIB::sys.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 사용자정보에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 사용자정보
    } else {
        lfn_SetFormAuth(false); // 읽기 사용자정보
    }
    
    gfn_SearchAllDept(cbxIPGTMGNO, "Y", "전체"); // 지부 코드 조회
	
	cbxIPGTMGNO.Index = 0;
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{ 
}
 

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioList.ClearData(); // 데이타셋 초기화
    
    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:searchIpDeptMappingList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioList=ds_ioList"	// outDs
		,"IPGTMGNO=" + quote(cbxIPGTMGNO.Value) // args 
		,"lfn_AfterSearch"
		,false
	);
}

/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
	if (!gfn_Confirm(MSGI00070)) { // 신규 추가 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.
        return false;
	}

	mb_IsNewData = true; // 저장버튼이 클릭 설정
    lfn_Cancel(); // 값 초기화

    // 기본값 설정
    chkIPUSERYN.Value = "Y";
}

/*
 * 기능 : 삭제
 */
function lfn_Delete(obj) {
    if (ds_ioList.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }

	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
        return false;
	}	
	
	
    ds_ioList.DeleteRow(ds_ioList.currow); // 선택된 ROW를 삭제한다.

    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "sys:deleteIpDeptMapping"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_ioList=ds_ioList:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSave"
		,false
	);
	
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {

	if (!gfn_FormValidate(this)) { // 입력 Validation Check
		return false;
	}
	
	tit_clearActionInfo();
	
	if (mb_IsNewData) { // 신규 저장
        // 키 중복 체크
    
        if (!gfn_Confirm(MSGW00020) ) { // 정말로 추가 저장 하시겠습니까?
			return false;
		}
		 			
		// 입력된 값을 그리드로 복사한다.
		ds_ioList.InsertRow(0);
		ds_ioList.SetColumn(0, "IPUSERYN", chkIPUSERYN.Value);
		ds_ioList.SetColumn(0, "IPGTMGNO", cbxIPGTMGNO.Value);
		ds_ioList.SetColumn(0, "IPDEPTTEL", edIPDEPTTEL.Text);
		ds_ioList.SetColumn(0, "IPQUEUE", edIPQUEUE.Text);
		ds_ioList.SetColumn(0, "IPGROUP", edIPGROUP.Text);
		ds_ioList.SetColumn(0, "IPSKILL", edIPSKILL.Text); 	
	
        
    } else { // 수정 저장
        if (!gfn_Confirm(MSGW00040)) { // 정말로 수정 저장 하시겠습니까?
			return false;
		}
		// 입력된 값을 그리드로 복사한다.
		ds_ioList.SetColumn(ds_ioList.currow, "IPUSERYN", chkIPUSERYN.Value);
		ds_ioList.SetColumn(ds_ioList.currow, "IPGTMGNO", cbxIPGTMGNO.Value);
		ds_ioList.SetColumn(ds_ioList.currow, "IPDEPTTEL", edIPDEPTTEL.Text);
		ds_ioList.SetColumn(ds_ioList.currow, "IPQUEUE", edIPQUEUE.Text);
		ds_ioList.SetColumn(ds_ioList.currow, "IPGROUP", edIPGROUP.Text); 
		ds_ioList.SetColumn(ds_ioList.currow, "IPSKILL", edIPSKILL.Text); 
		 
    }
 	  
    gfn_FormInit(this); // 입력란 초기화

	tit_clearActionInfo();
	tit_addSaveActionInfo(
		"sys:insertIpDeptMapping"	// insert
		, "sys:updateIpDeptMapping"	// update
		, ""	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_ioList=ds_ioList:U"	// inDs
		,""	// outDs
		,""	// args 
		,"lfn_AfterSave"
		,false
	);
	
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }  
	
    gfn_SearchMessage(gdList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSave(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 조회
}

/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdList, 0); // 멀티 정렬을 실행한다.
}

function ds_ioList_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        chkIPUSERYN.Value = obj.GetColumn(nRow, "IPUSERYN");
        cbxIPGTMGNO.Value = obj.GetColumn(nRow, "IPGTMGNO");
        edIPDEPTTEL.Text = obj.GetColumn(nRow, "IPDEPTTEL");
        edIPQUEUE.Text = obj.GetColumn(nRow, "IPQUEUE");
        edIPGROUP.Text = obj.GetColumn(nRow, "IPGROUP");        
        edIPSKILL.Text = obj.GetColumn(nRow, "IPSKILL");
        // 키값 수정 금지
        gfn_MakeKeyMark(this); // 키값 수정 금지 설정
	}
	mb_IsNewData = false; // 저장버튼이 클릭 초기화
}

function gdList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var lobj_Dataset = object(obj.BindDataset);
	ds_ioList_OnRowPosChanged(lobj_Dataset,nRow,nRow);
}
