            cbxCpersonGubun1.Enable=false;
        }
        cbxCourseD.Enable = false;
        cbxCourseD1.Enable = false;
        cbxCourseD2.Enable = false;
        cbxCourseD3.Enable = false;
        cbxCourseD4.Enable = false;
        cbxCourseD5.Enable = false;
        cbxCourseD6.Enable = false;
        cbxCourseD.index = 0;
        cbxCourseD1.index = 0;
        cbxCourseD2.index = 0;
        cbxCourseD3.index = 0;
        cbxCourseD4.index = 0;
        cbxCourseD5.index = 0;
        cbxCourseD6.index = 0;

	}
}

/*
 * 인력구분 필터링
 */
function lfn_FilterPersonGubun(sFilterGubun) {

	if(gfn_IsNull(sFilterGubun)) {
        sFilterGubun = 99;
    }

	ds_oPersonGubun.UnFilter();
	var fGubun;		//필터구분(직능)
	switch(sFilterGubun)
	{
		//공공기관
		case 13 :
			fGubun = "10";
		break;
		case 14 :
			fGubun = "10";
		break;
		//위험물
		case 30 :
			fGubun = "30";
		break;
		//소방기술자
		case 50 :
			fGubun = "50";
		break;
		//개인회원
		case 60 :
			fGubun = "60";
		break;
        default :
            fGubun = "99";
        break;
	}

	var ls_FilterExpr = "";
    if (!gfn_IsNull(fGubun)) {
        ls_FilterExpr += "PCD=='" + trim(fGubun) + "'";
    }
    ds_oPersonGubun.Filter(ls_FilterExpr);

    if(fGubun == "99") {
        radCpersonGubun.Index = 0;
        radCpersonGubun.Enable = false;
        radCpersonGubun_OnChanged(object("radCpersonGubun"),radCpersonGubun.Value);
    }
}
