//용도 라디오 버튼
function radBusinessType_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "1") { // 선택 
		cbxBusinessType1.Index = "0"; 
		cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
        cbxBusinessType1.Enable = true;
        cbxBusinessType2.Enable = true;                       
        cbxBusinessType1.SetFocus();
	} else {
        cbxBusinessType1.Index = "0"; 
		cbxBusinessType2.Index = ds_oAdditionInfo.rowcount - 1;
        cbxBusinessType1.Enable = false;
        cbxBusinessType2.Enable = false;                       	
	}
}

//회원번호
function radMgno_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "1"){
		medMgno1.SetFocus();
		medMgno1.Enable = true;
		medMgno2.Enable = true;
		medMgno1.Value = "";
		medMgno2.Value = "";		
	}else{
		medMgno1.Enable = false;
		medMgno2.Enable = false;
		medMgno1.Value = "";
		medMgno2.Value = "";				
	}
}

//선임일자
function radDate_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "1"){
		calStartDate.SetFocus();
        calStartDate.Value = left(GetDate(), 4)+"0101";   //calDateStart.Value = left(GetDate(),4)+"0101"
        calEndDate.Value = left(GetDate(), 8); 
		calStartDate.Enable = true;
		calEndDate.Enable = true;
	}else{
        calStartDate.Value = "";   //calDateStart.Value = left(GetDate(),4)+"0101"
        calEndDate.Value = ""; 
		calStartDate.Enable = false;
		calEndDate.Enable = false;
	}
}

