function radRegYYMM_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strCode == "1"){
		calStartRegYYMM.SetFocus();
        calStartRegYYMM.Value = left(GetDate(), 4)+"0101";   //calStartRegYYMM.Value = left(GetDate(),4)+"01"
        calEndRegYYMM.Value = left(GetDate(), 8); 
		calStartRegYYMM.Enable = true;
		calEndRegYYMM.Enable = true;
	}else{
        calStartRegYYMM.Value = "";   //calStartRegYYMM.Value = left(GetDate(),4)+"01"
        calEndRegYYMM.Value = ""; 
		calStartRegYYMM.Enable = false;
		calEndRegYYMM.Enable = false;
	}
}

