function btnToExcel_OnClick(obj)
{	
    lfn_Search();
	if(ds_oCustomerList.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}

	var strText = radReport.Text;

	gdList1.UserData = strText;
	gdList2.UserData = strText;

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1460";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oCustomerList.GetRowCount();

		if(radMinapfee.Value == 0) {
			parValue += " sTitle=" + quote(gdList1.UserData);			// 미납출력 안함
		} else if (radMinapfee.Value == 1) { 
			parValue += " sTitle=" + quote(gdList2.UserData);			// 미납출력 함
		}
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}

	//ClearClipboard(); // 클립보드를 Clear합니다.

	if(radExcel.Value == 1){
		//     2010.04.13 김민호  :  미납금액 출력 여부 
		if(radMinapfee.Value == 0) {
			setExcelGridHeader(gdList1,strText);			// 관리대상 미제외 , 미납출력 안함

		} else if (radMinapfee.Value == 1) { 
			setExcelGridHeader(gdList2,strText);			// 관리대상 미제외 , 미납출력 함
		}
		//     2010.04.13 김민호  :  미납금액 출력 여부 
	}else if(radExcel.Value == 2){					
		fdExport.Type = "save";
		fdExport.Filter = "Excel(.xls)|*.xls|";  //+ ' KFSA\KEMS\data\edu'
		var result = fdExport.Open("C:\\KFSA\\KEMS\\DATA\\EDU");  // C:\KFSA\KEMS\data\edu 

		if (result) {
			var ls_FilePath = fdExport.FilePath + '\\' + fdExport.FileName;  			  

			if(radMinapfee.Value == 0) {
				gdList1.SaveExcelEx(ls_FilePath, strText,,,,,true,false );  

			} else if (radMinapfee.Value == 1) { 
				gdList2.SaveExcelEx(ls_FilePath, strText,,,,,true,false ); 
			}

		} else {
			return;
		}      

	}else if(radExcel.Value == 3){					
		if (fdExport.Open()) {
			FileName = fdExport.FilePath + "\\" + fdExport.FileName;
			var file = split(fdExport.FileName, ".");
			var filepath = fdExport.FilePath +"\\";

			if(G_isNull(file[1])) {
				file[1] += ".bin"; //확장자를 임의로 변경하여 저장합니다. 
			}
			else
			{
				file[0] += ".";
			}

			str_FileName = filepath+file[0] + file[1];

			//grd_ChangeListChild.SaveExcel(str_FileName, "sheet1","A1",false,false,false);

			if(radMinapfee.Value == 0) {
				gdList1.SaveExcelEx(str_FileName, strText,,,,,true,false );  

			} else if (radMinapfee.Value == 1) { 
				gdList2.SaveExcelEx(str_FileName, strText,,,,,true,false ); 
			}
			setTimer(1,1); //임의변경한 확장자를 다시 엑셀포맷으로 변경합니다.
		} 

	}
}
function G_isNull(as_source)
{
	if (length(toString(as_source)) == 0 || as_source == null) {
		return true;
	}
	else {
		return false;
	}
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function setExcelGridHeader(grdObj,strText){
	create("grid","grdExcel",'BindDataSet="' + grdObj.BindDataset +'"');
	grdExcel.contents = grdObj.Contents;	
	var headCnt = getHeadCount(grdExcel);
	var totCol  = grdExcel.GetColCount();
	var irow;
	for(var i = 0 ; i < headCnt ; i++ )	{
		irow = toInteger(grdExcel.GetCellProp("head",i,"row"));
		grdExcel.SetCellProp("head",i,"row",irow+1);
	}
	var str_temp = grdExcel.GetCurContents();
	var str_Head = '<cell align="left" col="0" colspan="' + totCol + '" display="text" text="' + strText + '"/>';	
	str_temp = replace(str_temp, '</head>',str_Head +'</head>');
	grdExcel.contents = str_temp;
	grdExcel.ExportExcelEx("sheet1","A1",false,true,true);
	Destroy(grdExcel);
}
//엑셀 함수 입니다. (위에 제목을 넣기 위함)
function getHeadCount(grdObj){
	var strConts = grdObj.Contents;	
	var iPos = pos(strConts,"<head>");
	var strHead = Mid(strConts,iPos);
	iPos = pos(strHead,"</head>");
	strHead = Mid(strHead,0,iPos+7);
	var spObj = split(strHead,chr(10),"webstyle");
	return spObj.length()-2;
}
