var ls_SaveFileName = "";
var ls_RealFileName = "";
var ls_FilePath = "";
var ls_USEYN = "";


/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
 
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
		ls_FormId = split(ls_FormId[1], ".");
		
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
    gfn_MakeRequiredInputMark(this); 	 // 필수 입력란을 설정한다.
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } 
    else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } 
    else {
        gfn_AleDrt(MSGI00080);
        close();
    }
    
    // 공통 코드 조회
    gfn_SearchCommonCode(ds_oMunjeGubun, "MUNJEGUBUN", "Y", ""); // 인터넷접수상태 조회
    cbxMunjeGubun.Value = "N";    
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y"); // 직능 코드 조회
    
    //trace ("shjung::: ds_oCourse ::: " + ds_oCourse.SaveXML());
    cbxCourse.Value = "22";
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
    
    gfn_SearchCommonCode(ds_oInternetJubsuStatus, "IJUBSUGUBUN", "Y", "선택"); // 인터넷접수상태 조회
    cbxInternetJubsuStatus = "03"; // 접수예정
    gfn_SearchCommonCode(ds_oCourseGubun, "COURSEGUBUN", "Y", "");
    cbxCourseGubun.Value = "3";
    ds_oTrainingFee.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();

	tit_addCsvSearchActionInfo("com:searchTrainingFeeCode", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingFee=ds_oTrainingFee"	// outDs
		,""	// args 
		,""
		,false
		,false
		,true
	);
	
	// 해당 직능의 수수료로 설정
	ds_oTrainingFee.Filter("GUBUN=='"+cbxCourse.Value+cbxMunjeGubun.Value+"'");
    edTrainingYear.Text = left(GetDate(), 4);
    
    // 사이버교육기간    
    if (cbxMunjeGubun.Value == "C") {	// N:일반, Y:면제, C:혼합(사이버+소집)
		lbCyberDate.BkColor = "peru";
		calCyberDateStart.Enable = true;
		calCyberDateEnd.Enable = true;
    } 
    else {
		lbCyberDate.BkColor = "steelblue";
		calCyberDateStart.Enable = false;
		calCyberDateEnd.Enable = false;
		calCyberDateStart.Value = "";
		calCyberDateEnd.Value = "";
    }
    radSMSSendYN.Value = "0";
    radSMSSendYN_OnChanged(obj, "0");	
    edTrainingOrder.SetFocus();
    lfn_Search();
    lfn_change(); // 인터넷 좌석선택 여부확인 
    
	// 시스템관리자만 TOMGNO 보이도록 설정
	if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM") {
		lbImsiTOMGNO.Visible = true;
		edImsiTOMGNO.Visible = true;
		edImsiTOMGNO.Text = "";
	} 
	else {
		lbImsiTOMGNO.Visible = false;
		edImsiTOMGNO.Visible = false;
		edImsiTOMGNO.Text = "";
	} 
	
	// 초기 설정은 해당 ID의 지부가 우선 순위로 조회되는데
	// 이후 다른 지부의 데이터를 확인할 수 있도록 JIBU 콤보박스를 ENABLE = TRUE; 시킨다 
	// 김동한 23.06.29
	cbxJibu.Enable = true;
}


function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do 
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}
 

function Form_OnSize(obj,nCx,nCy,nState) {
    if (gs_IsResizingForm == "Y") {
        gdTrainingOrder.Height = nCy - gdTrainingOrder.Top - 4;
        gdTrainingOrder.Width = nCx - gdTrainingOrder.Left - 4;
    }
}


/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    
   if (edTrainingYear.Text == "") { // 입력 Validation Check
        gfn_Alert("년도를 입력하십시오.");
        edTrainingYear.SetFocus();
        return false;
    }
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",직능=" + quote(cbxCourse.Text);

    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    ls_USEYN = "";
    medTotalCnt.Value = "";
    
    ds_ioTrainingOrder.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();    
	tit_addCsvSearchActionInfo("training:searchTrainingOrder", false);		
	// 서버 호출 
	
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioTrainingOrder=ds_oTrainingOrder"	// outDs
		,"TOMGJIBU=" + quote(cbxJibu.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TCCOURSECD=" + quote(cbxCourse.Value)// args 
		,"lfn_AfterSearchTrainingOrder"
		,false
	);	
	
}


/*
 * 기능 : 추가
 */
function lfn_Input(obj) {
    
    if (!gfn_Confirm("신규 일정을 등록 하시겠습니까?\n입력완료 후 저장 버튼을 누르세요.")) {
		return false;
    }
    mb_IsNewData = true; // 저장버튼이 클릭 설정
    lfn_Cancel(); // 값 초기화
    // 기본값 설정    
    chkSexLimitYN.Value = "N";
    chkSeatLimitYN.Value = "N";
    calEduDateStart.Value = GetDate();
    calEduDateEnd.Value = GetDate();
    tmEduTimeStart.Value = "0900";
    tmEduTimeEnd.Value = "1800";
    var sOldOrder = 0;

    for (var i = 0; i < ds_ioTrainingOrder.GetRowCount(); i++) { 
		if (sOldOrder < ToInteger(ds_ioTrainingOrder.GetColumn(i, "TOTRAININGORDER"))) {
			sOldOrder = ToInteger(ds_ioTrainingOrder.GetColumn(i, "TOTRAININGORDER"));
		} 
    }    
    edTrainingOrder.Text = sOldOrder + 1;//회차
    //edTrainingOrder.Text = ToInteger(ds_ioTrainingOrder.GetColumn(0, "TOTRAININGORDER")) + 1; 
    edTrainingOrder_OnKillFocus(edTrainingOrder);    
    cbxMunjeGubun_OnChanged(obj,cbxMunjeGubun.Value);
    cbxFee.Index = 0;	
    radSMSSendYN.Value = "0";	// 안내문자 미발송
    radSMSSendYN_OnChanged(obj, "0");    
    radInetWaitYn.Value = "Y";
    edTrainingOrder.SetFocus(); 
    radInetJubsuPwuseYn.Value = "N";
    radInetJubsuPwuseYn_OnChanged(obj,radInetJubsuPwuseYn.Value); 
}


/*
 * 기능 : 삭제
 */ 
function lfn_Delete(obj) {

    if (ds_ioTrainingOrder.currow < 0) {
        gfn_Alert(MSGI00030); // 삭제할 레코드가 없습니다.
        return false;
    }
/*
    if (ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOJUBSUMAXCNT") != 0) {
        gfn_Alert("삭제가 불가능합니다."); // 삭제할 레코드가 없습니다.
        return false;
	}
*/	
    var sTOMGNO = ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOMGNO");	
    var sTCCOURSECD = ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TCCOURSECD");	
    var sTOMUNJEGUBUN = ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOMUNJEGUBUN");
    ds_ioJubsu.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("training:searchTrainingJubsuCnt", false);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioJubsu=ds_oJubsu"	// outDs
		,"TOMGNO=" + quote(sTOMGNO)// args 
		,""
		,false
		,""
		,true
	);
	
	if (ds_ioJubsu.GetCount() > 0) {
        gfn_Alert("접수자가 있어서 삭제가 불가능합니다."); // 삭제할 레코드가 없습니다.
		return false;
	}
	if (!gfn_Confirm(MSGW00030)) { // 정말로 삭제 하시겠습니까?
	    return false;
	}	
	
    lfn_SaveModifyHistory("D"); // 변경이력을 저장한다.
    ds_ioTrainingOrder.DeleteRow(ds_ioTrainingOrder.currow); // 선택된 ROW를 삭제한다.
    gfn_FormInit(this); // 입력란 초기화    
/*
    tit_clearActionInfo();
	tit_addSingleActionInfo("training:deleteTrainingOrder", 0, "");
	var arg = "TOMGNO=" + quote(sTOMGNO)
			+ " TCCOURSECD=" + quote(sTCCOURSECD)
			+ " TOMUNJEGUBUN=" + quote(sTOMUNJEGUBUN); 	// 수납환불구분
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"" // inDs
        ,""	// outDs
        ,arg
        ,"lfn_AfterSaveTrainingOrder"
        ,false
        ,false
        ,true
    );	
*/
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		""	// insert
		, ""	// update
		, "training:deleteTrainingOrder"	// delete
		, ""	// flag column 명
		, ""	// key sql id
		, 0	// key 증가 값
		, "" 
		, 0 
		, "N"	// 실행타입
	);
	// 서버 호출 
	tit_callService(
		""
		,""
		,"ds_iTrainingOrder=ds_ioTrainingOrder:U"	// inDs
		,""	// outDs
		,"" // args
		,"lfn_AfterSaveTrainingOrder"
		,false
	);
}


/*
 * 기능 : 저장
 */
function lfn_Save(obj) {

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
   
	if(gds_oUserInfo.GetColumn(0, "PSABUN") == "20220952" or gds_oUserInfo.GetColumn(0, "PDEPTCD") == "0251")
	{
	
	}
	else if(cbxjibu.Value <> gds_oUserInfo.GetColumn(0, "PDEPTCD"))
	{
		gfn_Alert(" 해당 지부의 소속이 아닙니다. 저장할 수 없습니다.");
		return; 
	}
	
    if (calEduDateStart.Value > calEduDateEnd.Value) {
        gfn_Alert("교육종료일이 시작일보다 늦습니다.");
        calEduDateEnd.SetFocus();
        return false;
    }
       
    // 안내문자 자동발송
    if (radSMSSendYN.Value == "1") {    
    
		if (gfn_isNull(calSendDate.Value)) {	
			gfn_Alert("안내문자 자동발송일이 입력되지 않았습니다.");	
			calSendDate.SetFocus();	
			return false;	
		}    		
		if (!gfn_isNull(calEduDateStart.Value) && (calEduDateStart.Value <= calSendDate.Value)) {	
			gfn_Alert("안내문자 자동발송일이 교육시작일보다 늦습니다.");	
			calSendDate.Value = substr(AddDate(calEduDateStart.Value,-2) ,0,8);	
			calSendDate.SetFocus();	
			return false;
		}
	}
	
	// 좌석수와 접수최대인원수 체크(2015년 교육부터 적용)
	if (edTrainingYear.Text >= "2015") {	
		if (lfn_JubsuCntCheck(cbxJibu.Value, "0", cbxCourse.Value) == false) {		
			medSeatCnt.SetFocus();			
			return false;			
		}		
	}
	
    if (gfn_isNull(cbxFee.Text)) {    
        gfn_Alert("수수료가 입력되지 않았습니다.");
        calEduDateStart.SetFocus();
        return false;       
    }
    
	// 수수료 오류 방지 위해 수수료 금액 다시 세팅
	for (var i = 0; i < ds_oTrainingFee.GetRowCount(); i++) {
	
		if (left(calEduDateStart.Value,8) >= ds_oTrainingFee.GetColumn(i, "TFCSTARTDATE") &&
			left(calEduDateStart.Value,8) <= ds_oTrainingFee.GetColumn(i, "TFCENDDATE")) {			
			cbxFee.Index = i;
			break;
		}
	}	
	
	if (!gfn_isNull(calJubsuStartDate.Value) && gfn_isNull(medJubsuStartTime.Value)) {
		gfn_Alert("접수시작일시의 시간이 입력되지 않았습니다.");
		medJubsuStartTime.SetFocus();
		return false;		
	}
	
	if (gfn_isNull(calJubsuStartDate.Value) && !gfn_isNull(medJubsuStartTime.Value)) {	
		gfn_Alert("접수시작일자가 입력되지 않았습니다.");
		calJubsuStartDate.SetFocus();
		return false;
		
	}
	
	if (!gfn_isNull(calJubsuEndDate.Value) && gfn_isNull(medJubsuEndTime.Value)) {	
		gfn_Alert("접수마감일시의 시간이 입력되지 않았습니다.");
		medJubsuEndTime.SetFocus();
		return false;		
	}
	
	if (gfn_isNull(calJubsuEndDate.Value) && !gfn_isNull(medJubsuEndTime.Value)) {	
		gfn_Alert("접수마감일자가 입력되지 않았습니다.");
		calJubsuEndDate.SetFocus();
		return false;		
	}
	
	if (mb_IsNewData) { // 신규 저장        
		// 키 중복 체크    
        if (!gfn_Confirm("신규 일정을 저장하시겠습니까?\n\n" +
						 " ▶ 일정 : " + left(calEduDateStart.Value,4)+"."+substr(calEduDateStart.Value,4,2)+"."+substr(calEduDateStart.Value,6,2)
						  + " ~ " + left(calEduDateEnd.Value,4)+"."+substr(calEduDateEnd.Value,4,2)+"."+substr(calEduDateEnd.Value,6,2) + "\n\n" +
						 " ▶ 회차 : " + edTrainingOrder.Text + "\n\n" +
						 " ▶ 수수료 : " + cbxFee.Text+"원")) { // 정말로 추가 저장 하시겠습니까?
            return false;
        }
        
        ds_oTrainingOrderMgno.ClearData(); // 데이타셋 초기화
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchTrainingOrderMgno", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oTrainingOrderMgno=ds_oTrainingOrderMgno"	// outDs
            ,"TOMGJIBUCD=" + quote(cbxJibu.Value)
                + " TOYEAR=" + quote(edTrainingYear.Text)
                + " TCCOURSECD=" + quote(cbxCourse.Value + cbxMunjeGubun.Value)
                + " TOORDER=" + quote(edTrainingOrder.Text)// args 
            ,""
            ,false
            ,""
            ,true
        );
        
        if (ds_oTrainingOrderMgno.GetCount() >= 1) {
            gfn_Alert("중복된 회차입니다.");
            edTrainingOrder.SetFocus();
            return;
        }
            
        // 입력된 값을 그리드로 복사한다.
        ds_ioTrainingOrder.InsertRow(0);
        ds_ioTrainingOrder.SetColumn(0, "TOMGJIBUCD", cbxJibu.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOYEAR", edTrainingYear.Text);
        ds_ioTrainingOrder.SetColumn(0, "TCCOURSECD", cbxCourse.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOMUNJEGUBUN", cbxMunjeGubun.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOCOURSEGUBUN", cbxCourseGubun.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOTRAININGORDER", edTrainingOrder.Text);
        ds_ioTrainingOrder.SetColumn(0, "TOSTARTDATE", left(calEduDateStart.Value, 8));
        ds_ioTrainingOrder.SetColumn(0, "TOENDDATE", left(calEduDateEnd.Value, 8));
        ds_ioTrainingOrder.SetColumn(0, "TOSTARTTIME", tmEduTimeStart.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOENDTIME", tmEduTimeEnd.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOSEATCNT", medSeatCnt.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOJUBSUMAXCNT", medJubsuCnt.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOINETJUBSUCNT", medIJubsuCnt.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOINETJUBSUYN", radInetJubsuYn.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOAWAITERCNT", medInetWaitSeatCnt.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOAWAITERREGCNT", medInetWaitRegCnt.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOAWAITERYN", radInetWaitYn.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOSEATSELECTYN", chkISeatSelectYn.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOSEXLIMITYN", chkSexLimitYN.Value);
        ds_ioTrainingOrder.SetColumn(0, "TOSEATLIMITYN", chkSeatLimitYN.Value);
        ds_ioTrainingOrder.SetColumn(0, "EPMGNO", ls_EduPlaceSeq);
        ds_ioTrainingOrder.SetColumn(0, "TOREMARK", edAddNoticeRemark.Text);
        ds_ioTrainingOrder.SetColumn(0, "TOEXCEPTDATE", edEduExceptDate.Text);
        //ds_ioTrainingOrder.SetColumn(0, "TOMAILNOTICE", edMailNotice.Text);	//2016.04.04 제외
        ds_ioTrainingOrder.SetColumn(0, "TOJUBSUSTATUS", cbxInternetJubsuStatus.Value);
        ds_ioTrainingOrder.SetColumn(0, "USE_YN", radTrainingOrderUseYn.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TODAYGUBUN", radDayGubun.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOCYBERSTARTDATE", calCyberDateStart.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOCYBERENDDATE", calCyberDateEnd.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOTFCSEQ", cbxFee.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOAUTOMSGGUBUN", radSMSSendYN.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOAUTODATE", left(calSendDate.Value, 8));        
        ds_ioTrainingOrder.SetColumn(0, "TOJUBSUSTARTDATE", left(calJubsuStartDate.Value, 8)+medJubsuStartTime.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOJUBSUENDDATE", left(calJubsuEndDate.Value, 8)+medJubsuEndTime.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOONLINEGUBUN", cbxOnlineGubun.Value);        
        ds_ioTrainingOrder.SetColumn(0, "TOONLINEID", edOnlineID.Text);        
        ds_ioTrainingOrder.SetColumn(0, "TOONLINEPWD", edOnlinePWD.Text);
        //2023.06.14 대면교육일정 컬럼추가
        ds_ioTrainingOrder.SetColumn(0, "TODSTARTDATE", left(calStartdate.Value, 8));        
        ds_ioTrainingOrder.SetColumn(0, "TODENDDATE", left(calEnddate.Value,8));
        //2023.10.10 인터넷접수비밀번호 추가          
        ds_ioTrainingOrder.SetColumn(0, "TOINETJUBSUPWUSEYN", radInetJubsuPwuseYn.Value);          
        ds_ioTrainingOrder.SetColumn(0, "TOINETJUBSUPWD", edInetJubsuPwd.Text);
       
        lfn_SaveModifyHistory("I"); // 변경이력을 저장한다.
    } 
    else { // 수정 저장
    
        ds_oModifyTime.ClearData(); // 데이타셋 초기화
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchTrainingOrderModifyTime", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oModifyTime=ds_oModifyTime"	// outDs
            ,"TOMGNO=" + ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOMGNO") // args 
            ,""
            ,false
            ,""
            ,true
        );
        
        if (ds_oModifyTime.getColumn(0, "MODIFYTIME") == "MODIFYNO") {        
            gfn_Alert("접수시작일시부터 30분간은 수정하실 수 없습니다.");
            return false;                
        }
    
		if ((ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOSTARTDATE") <= left(getDate(),8)) ||
		    (ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOENDDATE") <= left(getDate(),8))) {
		    
			if (ls_USEYN == radTrainingOrderUseYn.Value) {	// 사용여부가 같을 경우		    
				gfn_Alert("지난 일정은 수정하실 수 없습니다.");
				return false;				
			}
        }

		// 대기자가 있는 경우
		lfn_searchAcceptAwaiterCnt(ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOMGNO"));
		
		if (ds_oAcceptAwaiter.GetColumn(0, "AWAITERCNT") > 0 ) {			
			if (radInetJubsuYn.Value == "N") {			
				gfn_Alert("대기자가 있는 경우 인터넷접수를 '미사용'으로 변경하실 수 없습니다.");				
				return false;
			}			
		}
		
        if (!gfn_Confirm("일정을 수정하시겠습니까?\n\n" +
						 " ▶ 일정 : " + left(calEduDateStart.Value,4)+"."+substr(calEduDateStart.Value,4,2)+"."+substr(calEduDateStart.Value,6,2)
						  + " ~ " + left(calEduDateEnd.Value,4)+"."+substr(calEduDateEnd.Value,4,2)+"."+substr(calEduDateEnd.Value,6,2) + "\n\n" +
						 " ▶ 회차 : " + edTrainingOrder.Text + "\n\n" +
						 " ▶ 수수료 : " + cbxFee.Text+"원")) { // 정말로 수정 저장 하시겠습니까?
            return false;
        }
        // 입력된 값을 그리드로 복사한다.
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOMGJIBUCD", cbxJibu.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOYEAR", edTrainingYear.Text);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TCCOURSECD", cbxCourse.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOMUNJEGUBUN", cbxMunjeGubun.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOCOURSEGUBUN", cbxCourseGubun.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOTRAININGORDER", edTrainingOrder.Text);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSTARTDATE", left(calEduDateStart.Value, 8));
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOENDDATE", left(calEduDateEnd.Value, 8));
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSTARTTIME", tmEduTimeStart.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOENDTIME", tmEduTimeEnd.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSEATCNT", medSeatCnt.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOJUBSUMAXCNT", medJubsuCnt.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOINETJUBSUCNT", medIJubsuCnt.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOINETJUBSUYN", radInetJubsuYn.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOAWAITERCNT", medInetWaitSeatCnt.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOAWAITERREGCNT", medInetWaitRegCnt.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOAWAITERYN", radInetWaitYn.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSEATSELECTYN", chkISeatSelectYn.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSEXLIMITYN", chkSexLimitYN.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOSEATLIMITYN", chkSeatLimitYN.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "EPMGNO", ls_EduPlaceSeq);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOREMARK", edAddNoticeRemark.Text);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOEXCEPTDATE", edEduExceptDate.Text);
        //ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOMAILNOTICE", edMailNotice.Text); //2016.04.04 제외
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOJUBSUSTATUS", cbxInternetJubsuStatus.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "USE_YN", radTrainingOrderUseYn.Value);
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TODAYGUBUN", radDayGubun.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOCYBERSTARTDATE", calCyberDateStart.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOCYBERENDDATE", calCyberDateEnd.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOTFCSEQ", cbxFee.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOREGION", edRegion.Text);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOAUTOMSGGUBUN", radSMSSendYN.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOAUTODATE", left(calSendDate.Value, 8));        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOJUBSUSTARTDATE", left(calJubsuStartDate.Value, 8)+medJubsuStartTime.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOJUBSUENDDATE", left(calJubsuEndDate.Value, 8)+medJubsuEndTime.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOONLINEGUBUN", cbxOnlineGubun.Value);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOONLINEID", edOnlineID.Text);        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOONLINEPWD", edOnlinePWD.Text);  
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TODSTARTDATE", left(calStartDate.Value,8));        
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TODENDDATE", left(calEndDate.Value,8)); 
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOINETJUBSUPWUSEYN", radInetJubsuPwuseYn.Value);          
        ds_ioTrainingOrder.SetColumn(ds_ioTrainingOrder.currow, "TOINETJUBSUPWD", edInetJubsuPwd.Text);
        
        lfn_SaveModifyHistory("U"); // 변경이력을 저장한다.
    }
    gfn_FormInit(this); // 입력란 초기화
    tit_clearActionInfo();
    tit_addSaveActionInfo(
        "training:insertTrainingOrder"	// insert
        , "training:updateTrainingOrder"	// update
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );  
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_iTrainingOrder=ds_ioTrainingOrder:U"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSaveTrainingOrder"
        ,false
    );
}


/* 공통 필수 종료 */
/* 사용자 함수 */
/*
 * 기능 : 교육일정관리 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingOrder(nErrorCode, strErrorMsg) {   	
   	
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }   
    
	// 해당 직능의 수수료로 설정
	ds_oTrainingFee.Filter("GUBUN=='"+cbxCourse.Value+cbxMunjeGubun.Value+"'"); 
	
	if (ds_ioTrainingOrder.rowcount > 0) {	
		gdTrainingOrder_OnCellClick(gdTrainingOrder, 0);		
	}
    medTotalCnt.Value = ds_ioTrainingOrder.rowcount;    
    gfn_SearchMessage(gdTrainingOrder); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    
}


/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveTrainingOrder(nErrorCode, strErrorMsg) {
    
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }    
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 교육일정관리 조회
}


/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnInput.Enable = bStatus; // 추가 버튼
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnSave.Enable = bStatus; // 수정&저장 버튼
}


