function ds_ioTrainingOrder_OnRowPosChanged(obj,nOldRow,nRow) {
    // 그리드의 값을 입력란에 복사한다. 추가상태인 경우 복사 후 저장을 하기 위해 제외한다.
    if (obj.GetRowType(nRow) <> "insert" && nRow <> -1) {
        cbxJibu.Value = obj.GetColumn(nRow, "TOMGJIBUCD");
        edTrainingYear.Text = obj.GetColumn(nRow, "TOYEAR");
        cbxCourse.Value = obj.GetColumn(nRow, "TCCOURSECD");        
        cbxMunjeGubun.Value = obj.GetColumn(nRow, "TOMUNJEGUBUN");        
        cbxCourseGubun.Value = obj.GetColumn(nRow, "TOCOURSEGUBUN");
        edTrainingOrder.Text = obj.GetColumn(nRow, "TOTRAININGORDER");
        calEduDateStart.Value = obj.GetColumn(nRow, "TOSTARTDATE");
        calEduDateEnd.Value = obj.GetColumn(nRow, "TOENDDATE");
        tmEduTimeStart.Value = obj.GetColumn(nRow, "TOSTARTTIME");
        tmEduTimeEnd.Value = obj.GetColumn(nRow, "TOENDTIME");
        medSeatCnt.Value = obj.GetColumn(nRow, "TOSEATCNT");
        medJubsuCnt.Value = obj.GetColumn(nRow, "TOJUBSUMAXCNT");
        medIJubsuCnt.Value = obj.GetColumn(nRow, "TOINETJUBSUCNT");        
        radInetJubsuYn.Value = obj.GetColumn(nRow, "TOINETJUBSUYN");        
        medInetWaitSeatCnt.Value = obj.GetColumn(nRow, "TOAWAITERCNT");        
        medInetWaitRegCnt.Value = obj.GetColumn(nRow, "TOAWAITERREGCNT");        
        radInetWaitYn.Value = obj.GetColumn(nRow, "TOAWAITERYN");        
        chkISeatSelectYn.Value = obj.GetColumn(nRow, "TOSEATSELECTYN");
        chkSexLimitYN.Value = obj.GetColumn(nRow, "TOSEXLIMITYN");
        chkSeatLimitYN.Value = obj.GetColumn(nRow, "TOSEATLIMITYN");
        ls_EduPlaceSeq = obj.GetColumn(nRow, "EPMGNO");
        edEduPlace.Text = obj.GetColumn(nRow, "EPNM");
        edParkingNotice.Text = obj.GetColumn(nRow, "EPPARKINGINFO");
        edAddNoticeRemark.Text = obj.GetColumn(nRow, "TOREMARK");
        edEduExceptDate.Text = obj.GetColumn(nRow, "TOEXCEPTDATE");
        //edMailNotice.Text = obj.GetColumn(nRow, "TOMAILNOTICE");	//2016.04.04 제외
        cbxInternetJubsuStatus.Value = obj.GetColumn(nRow, "TOJUBSUSTATUS");
        radTrainingOrderUseYn.Value = obj.GetColumn(nRow, "USE_YN");
        
        ls_USEYN = obj.GetColumn(nRow, "USE_YN");        
        radDayGubun.Value = obj.GetColumn(nRow, "TODAYGUBUN");        
        calCyberDateStart.Value = obj.GetColumn(nRow, "TOCYBERSTARTDATE");        
        calCyberDateEnd.Value = obj.GetColumn(nRow, "TOCYBERENDDATE");        
        cbxFee.Value = obj.GetColumn(nRow, "TOTFCSEQ");        
        edRegion.Text = obj.GetColumn(nRow, "TOREGION");        
        radSMSSendYN.Value = obj.GetColumn(nRow, "TOAUTOMSGGUBUN");        
        calSendDate.Value = obj.GetColumn(nRow, "TOAUTODATE");
        
        calJubsuStartDate.Value = left(obj.GetColumn(nRow, "TOJUBSUSTARTDATE"), 8);		// 접수시작일자        
        medJubsuStartTime.Value = substr(obj.GetColumn(nRow, "TOJUBSUSTARTDATE"), 8, 4);	// 접수시작시간       
        calJubsuEndDate.Value = left(obj.GetColumn(nRow, "TOJUBSUENDDATE"), 8);		// 접수마감일자        
        medJubsuEndTime.Value = substr(obj.GetColumn(nRow, "TOJUBSUENDDATE"), 8, 4);	// 접수마감시간        
        cbxOnlineGubun.Value = obj.GetColumn(nRow, "TOONLINEGUBUN");	//실시간 온라인교육 구분        
        edOnlineID.Text = obj.GetColumn(nRow, "TOONLINEID");	// 실시간 온라인 개설방ID        
        edOnlinePWD.Text = obj.GetColumn(nRow, "TOONLINEPWD");	// 실시간 온라인 개설방PWD        
        edImsiTOMGNO.Text = obj.GetColumn(nRow, "TOMGNO");                
        calStartdate.Value = obj.GetColumn(nRow, "TODSTARTDATE");
        calEndDate.Value = obj.GetColumn(nRow, "TODENDDATE");             
             
        radSMSSendYN_OnChanged(obj,radSMSSendYN.Value);        
        cbxMunjeGubun_OnChanged(obj,cbxMunjeGubun.Value);        
        radInetJubsuYn_OnChanged(obj,radInetJubsuYn.Value);         
		cbxOnlineGubun_OnChanged(obj,cbxOnlineGubun.Value);  // 실시간온라인교육 여부 변경 시
		
		// OnChanged 후 초기화되어 다시 셋팅 
        calCyberDateStart.Value = obj.GetColumn(nRow, "TOCYBERSTARTDATE");        
        calCyberDateEnd.Value = obj.GetColumn(nRow, "TOCYBERENDDATE"); 
        calStartdate.Value = obj.GetColumn(nRow, "TODSTARTDATE");
        calEndDate.Value = obj.GetColumn(nRow, "TODENDDATE");       
  
        radInetJubsuPwuseYn.Value = obj.GetColumn(nRow, "TOINETJUBSUPWUSEYN");	// 인터넷접수패스워드사용여부        
        edInetJubsuPwd.Text = obj.GetColumn(nRow, "TOINETJUBSUPWD");	// 인터넷접수패스워드
         
        radInetJubsuPwuseYn_OnChanged(obj,radInetJubsuPwuseYn.Value); 
             
		gfn_MakeKeyMark(this); // 키값 수정 금지 설정  
     } 
     mb_IsNewData = false; // 저장버튼이 클릭 초기화
}


