/*
*  인터넷 좌석 선택이 아니오 이면 좌석성별표시를 enable로 함
*/
function lfn_change(nRow)
{
    if (ds_ioTrainingOrder.GetColumn(nRow, 'TOSEATSELECTYN') == 'Y') {
        chkSexLimitYN.Enable = true;
    } 
    else {
        chkSexLimitYN.Enable = false;
        chkSexLimitYN.value = 'N';    
    }
}


