function cbxMunjeGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	//초기화부터 
	lbCyberDate.BkColor = "steelblue";
	lbCyberDate.Text = "사이버교육기간";
	calCyberDateStart.UserData = "S:사이버교육기간";
	calCyberDateEnd.UserData= "S:사이버교육기간";
	lbdmDate.BkColor = "steelblue";
	calCyberDateStart.Enable = false;
	calCyberDateEnd.Enable = false;
	calStartdate.Enable = false;
	calEnddate.Enable = false;
	calCyberDateStart.Value = "";
	calCyberDateEnd.Value = "";
	calStartdate.Value = "";
	calEnddate.Value = "";
		
    // 사이버교육기간
    if (strCode == "C") {		// N:일반, Y:면제, C:혼합(사이버+소집)
		lbCyberDate.BkColor = "peru";
		calCyberDateStart.Enable = true;
		calCyberDateEnd.Enable = true;				
    }
	// 해당 직능의 수수료로 설정
	ds_oTrainingFee.Filter("GUBUN=='"+cbxCourse.Value+cbxMunjeGubun.Value+"'");	  
}


// 좌석수와 접수 최대인원수 비교
function lfn_JubsuCntCheck(sJibu, sWorkGubun, sCourse) {
	var vEduJubsuMaxCnt = 0;
	
	vEduJubsuMaxCnt = gfn_SearchEduJubsuMaxCnt(sJibu, sWorkGubun, sCourse);
	// 23.02 블라인드 일 경우 200명으로 체크 
	if(cbxOnlineGubun.Value = "X"){ // X : 블라인드접수
		vEduJubsuMaxCnt = 200;
	}
	// 2017년부터 적용(2016.11.25)
	//if (ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOENDDATE") > "20161231") {
		if (toNumber(medSeatCnt.Value) > toNumber(vEduJubsuMaxCnt)) {
			gfn_Alert("접수최대인원수를 초과하였습니다.\n\n"
					  +"최대 "+vEduJubsuMaxCnt+"명까지 접수가능합니다.");
			return false;	
		}
	//}
	
}


// 강습접수현황 바로가기
function gdTrainingOrder_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	var ArrMenuInfo = gfn_SearchMenuInfo(iif(gs_SystemGubun=="training","training","crs"), "training::frmtraining0030MTrainingJubsuList.xml");	//메뉴정보조회
	if(length(ArrMenuInfo) > 0 ) {
		var sArg = "sJibu=" + quote(ds_ioTrainingOrder.GetColumn(nRow, "TOMGJIBUCD"))
			+ " sYear=" + quote(ds_ioTrainingOrder.GetColumn(nRow, "TOYEAR"))        
			+ " sCourse=" + quote(ds_ioTrainingOrder.GetColumn(nRow, "TCCOURSECD"))        
			+ " sOrder=" + quote(ds_ioTrainingOrder.GetColumn(nRow, "TOTRAININGORDER"))        
			+ " sSunapGubun='NM'";
		
		gs_SystemGubun = arrMenuInfo[7];						//"cust";
		gs_SystemMenuId = arrMenuInfo[5];						//"0000000254";
		global.mnuLeft.tabMenu.tabpgMenu.Text = arrMenuInfo[6];	//"고객관리";
		var ls_MenuId = arrMenuInfo[0];							//"0000000273";
		var ls_MenuTitle = arrMenuInfo[1];						//"고객정보관리";
		var ls_MenuUrl = arrMenuInfo[2];						//"cust::frmcust0010MCustomerManagement.xml";
		
		global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
		gfn_OpenLinkChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId, sArg); 
	} 
	else {
		gfn_Alert("해당 메뉴를 찾을 수 없습니다.");
	}
}


// 안내문자발송 여부
function radSMSSendYN_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") {	// 미발송
		radSMSSendYN.BKColor = "white";
		lbSendDate.Enable = false;
		calSendDate.Enable = false;
		//calSendDate.Value = "";
		lbSendTime.Enable = false;
	} 
	else {	// 발송
		radSMSSendYN.BKColor = "goldenrod";
		lbSendDate.Enable = true;
		calSendDate.Enable = true;		
		lbSendTime.Enable = true;
	}
}


function radSMSSendYN_OnClick(obj)
{
	if (radSMSSendYN.Value == "0") {	// 미발송
		calSendDate.Value = "";
	} 
	else {
		if (gfn_isNull(ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOAUTODATE"))) {
			calSendDate.Value = substr(AddDate(calEduDateStart.Value,-2) ,0,8);
		}
		else {
			calSendDate.Value = ds_ioTrainingOrder.GetColumn(ds_ioTrainingOrder.currow, "TOAUTODATE");
		}
	}
}


function calSendDate_OnChanged(obj,strOldText,strNewText)
{
    // 안내문자 자동발송
    if (radSMSSendYN.Value == "1") {
    
		if (!gfn_isNull(calEduDateStart.Value) && (calEduDateStart.Value <= calSendDate.Value)) {	
			gfn_Alert("안내문자 자동발송일이 교육시작일보다 늦습니다.");	
			calSendDate.Value = substr(AddDate(calEduDateStart.Value,-2) ,0,8);	
			calSendDate.SetFocus();	
			return false;	
		}
     }
}


