function calEduDateStart_OnKillFocus(obj)  {
	for (var i = 0; i < ds_oTrainingFee.GetRowCount(); i++) {	
		if (left(calEduDateStart.Value,8) >= ds_oTrainingFee.GetColumn(i, "TFCSTARTDATE") &&
			left(calEduDateStart.Value,8) <= ds_oTrainingFee.GetColumn(i, "TFCENDDATE")) {
			
			cbxFee.Index = i;
			break;
		}
	}
}


function calEduDateEnd_OnKillFocus(obj)  {
	for (var i = 0; i < ds_oTrainingFee.GetRowCount(); i++) {
	
		if (left(calEduDateEnd.Value,8) >= ds_oTrainingFee.GetColumn(i, "TFCSTARTDATE") &&
			left(calEduDateEnd.Value,8) <= ds_oTrainingFee.GetColumn(i, "TFCENDDATE")) {			
			cbxFee.Index = i;
			break;
		}
	}	
}


