	}
}


function lfn_searchAcceptAwaiterCnt(sOrderMgno) {
   
    ds_oAcceptAwaiter.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchTraining0010AcceptAwaiterCnt", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oAcceptAwaiter=ds_oAcceptAwaiter"	// outDs
		,"TOMGNO=" + quote(sOrderMgno)	// args 
		,"lfn_AfterSearchAcceptAwaiterCnt"
		,false		
		,false		
		,true
	);
}


function lfn_AfterSearchAcceptAwaiterCnt(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}


function lfn_Date_OnKillFocus(obj)  {
	if (length(obj.Value) > 0 && length(obj.Value) < 8) {
			gfn_Alert("날자형식이 맞지 않습니다.");
			obj.Value = "";
			obj.SetFocus();	
	}
	
	if (!gfn_isNull(obj.Value)) {
		if (!gfn_IsDate(obj.Value)) { // 날자형식 체크
			gfn_Alert("날자형식이 맞지 않습니다.");
			obj.Value = "";
			obj.SetFocus();
		}
	}	
}


function lfn_time_OnKillFocus(obj)  {
	if (length(obj.Value) > 0 && length(obj.Value) < 4) {
		gfn_Alert("시간형식이 맞지 않습니다.");
		obj.Value = "";
		obj.SetFocus();	
	}
	
	if (!gfn_isNull(obj.Value)) {
		if (!gfn_IsTime(obj.Value)) {	// 시간형식 체크
			gfn_Alert("시간형식이 맞지 않습니다.");
			obj.Value = "";
			obj.SetFocus();
		}
	}
}


// 실시간온라인교육 여부 변경 시
function cbxOnlineGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex) {
	
	//if (strCode == "Y") { // 실시간온라인교육
	//if (strCode == "Y" || strCode == "X") { // 실시간온라인교육 + & 블라인드 신규추가 ( 기존 실시간 교육과 프로세스를 동일하게 한다 _ 김동한K 2022.09.07 ) 
	if (strCode == "Y" || strCode == "X"|| strCode == "Z") { //  23.07 혼용도 포함
		edOnlineID.Enable = true;
		edOnlinePWD.Enable = true;
	}
	else if(strCode == "N" ) { // 소집교육 + " 혼용 " 추가 ( 기존 소집교육과 동일한 프로세스로 적용한다. 김동한K, 2022.09.07 ) --> 23.07 혼용은 따로 
	//else {	// 소집교육 & 블라인드
		edOnlineID.Enable = false;
		edOnlinePWD.Enable = false;
		edOnlineID.Text = "";
		edOnlinePWD.Text = "";
	}	
	
	//  23.07 혼용교육이면 사이버교육, 대면교육 입력받게 
	//초기화부터	
	lbCyberDate.BkColor = "steelblue";
	lbCyberDate.Text = "사이버교육기간";
	calCyberDateStart.UserData = "S:사이버교육기간";
	calCyberDateEnd.UserData= "S:사이버교육기간";
	lbdmDate.BkColor = "steelblue";
	calCyberDateStart.Enable = false;
	calCyberDateEnd.Enable = false;
	calStartdate.Enable = false;
	calEnddate.Enable = false;
	calCyberDateStart.Value = "";
	calCyberDateEnd.Value = "";
	calStartdate.Value = "";
	calEnddate.Value = "";
	if (strCode == "Z") { //  23.07 혼용교육이면 사이버교육, 대면교육 입력받게 		
		lbCyberDate.BkColor = "peru";
		lbCyberDate.Text = "원격교육기간";
		calCyberDateStart.UserData = "S:원격교육기간";
		calCyberDateEnd.UserData= "S:원격교육기간";
		lbdmDate.BkColor = "peru";
		calCyberDateStart.Enable = true;
		calCyberDateEnd.Enable = true;	
		calStartdate.Enable = true;
		calEnddate.Enable = true;		
	}	
	
}
