/* 사용자 함수 */

/*

 * 기능 : 결강자 조회 후 처리해야 할 내용

 * parameter : nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchTrainingAbsent(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

	// 특급소방안전관리자의 경우 그리드 포맷 변경
	if (cbxCourse.Value == "10") { // 23.07 (특급 10일--> 20일 )
		
		gdTrainingAbsent.Format = "SPECIALGRADE";
		
	} else if (cbxCourse.Value == "21") { // 23.07 (1급 5일--> 10일 )
	
		gdTrainingAbsent.Format = "GRADE1";
	
	} else {
	
		gdTrainingAbsent.Format = "NORMAL";
	
	}

    medTotalCnt.Value = ds_oTrainingAbsent.GetCount();

    gfn_SearchMessage(gdTrainingAbsent); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

        

    var absentcnt = 0;	// 결강인원
    var passcnt = 0; 	// 수료인원
    var munjecnt = 0;	// 결강면제인원

	for(i = 0; i < ds_oTrainingAbsent.RowCount(); i++) {

        if (ds_oTrainingAbsent.GetColumn(i, "ADAY1") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY2") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY3") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY4") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY5") == 'N'
            
            || ds_oTrainingAbsent.GetColumn(i, "ADAY6") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY7") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY8") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY9") == 'N'
            
            || ds_oTrainingAbsent.GetColumn(i, "ADAY10") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY11") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY12") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY13") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY14") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY15") == 'N'
            
            || ds_oTrainingAbsent.GetColumn(i, "ADAY16") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY17") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY18") == 'N'

            || ds_oTrainingAbsent.GetColumn(i, "ADAY19") == 'N'
            
            || ds_oTrainingAbsent.GetColumn(i, "ADAY20") == 'N') {

            absentcnt += 1; // 결강인원 카운트

        }
        
        if (ds_oTrainingAbsent.GetColumn(i, "TJPASSYN") == 'Y') {
        
            passcnt += 1; // 수료인원 카운트
           
        }
        
        if (ds_oTrainingAbsent.GetColumn(i, "AMUNJEYN") == 'Y') {
        
            munjecnt += 1; // 결강면제인원 카운트
           
        }

    }

    medAbsenCnt.Value = absentcnt; // 결강인원 입력
    
    medPassCnt.Value = passcnt; // 수료인원 입력
    
    medMunjeCnt.Value = munjecnt; // 결강면제인원 입력

    

    // 결강자만 뽑아서 임시 데이터셋에 저장

    ds_oTrainingAbsentTemp.Clear();

    ds_oTrainingAbsentTemp.Copy(ds_oTrainingAbsent);    

    

    for (i=ds_oTrainingAbsent.GetRowCount();i>=0;i--) {

        if (ds_oTrainingAbsentTemp.GetColumn(i, "ADAY1") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY2") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY3") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY4") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY5") <> "N"
            
            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY6") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY7") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY8") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY9") <> "N"
            
            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY10") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY11") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY12") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY13") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY14") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY15") <> "N"
            
            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY16") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY17") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY18") <> "N"

            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY19") <> "N"
            
            && ds_oTrainingAbsentTemp.GetColumn(i, "ADAY20") <> "N") {

            

            ds_oTrainingAbsentTemp.DeleteRow(i);

        }

    }
    
    // 혼합과정일 경우 사이버교육이 1일차임을 안내
    if (ds_oTrainingAbsent.GetColumn(0, "TOMUNJEGUBUN") == "C") {
    
		lbCyberNotice.Visible = true;
	
	} else {
	
		lbCyberNotice.Visible = false;
	
	}

}



/*

 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용

 * parameter : strSvcID - 서비스ID

 *             nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSaveTrainingAbsent(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }



    gfn_SaveMessage(); // 저장 메세지를 처리한다.

    lfn_Search(); // 결강자 조회

}



