/* 회차 포커스 아웃될때 자동 0 입력 */

function edTrainingOrder_OnKillFocus(obj) {

	gfn_ComponentOnKillFocus(obj);

    

    obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));    

}



function lfn_SearchTrainingAbsent() {

    if (gfn_IsNull(cbxJibu.Value)

            || gfn_IsNull(cbxCourse.Value)

            || gfn_IsNull(edTrainingYear.Text)

            || gfn_IsNull(edTrainingOrderStart.Text)
            
            || gfn_IsNull(edTrainingOrderEnd.Text)) {

        return false;

    }

    edTrainingOrderStart.Text = iif(gfn_IsNull(edTrainingOrderStart.Text), "", lpad(edTrainingOrderStart.Text, "0", 3));
    
    edTrainingOrderEnd.Text = iif(gfn_IsNull(edTrainingOrderEnd.Text), "", lpad(edTrainingOrderEnd.Text, "0", 3));
    
	// 특급소방안전관리자의 경우 그리드 포맷 변경
	if (cbxCourse.Value == "10") {
		
		gdTrainingAbsent.Format = "SPECIALGRADE";
		
	} else {
	
		gdTrainingAbsent.Format = "NORMAL";
	
	}

    lfn_Search();

}



function btnToExcel_OnClick(obj) {

    if (ds_oTrainingAbsent.GetRowCount() < 1) {

        gfn_Alert("출력할 데이터가 존재하지 않습니다.");

        return false;

    }

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1106";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oTrainingAbsent.GetRowCount();
		parValue += " sTitle=" + quote(gdTrainingAbsent.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);

}


