}


// 결강면제 처리
function btnMunjeSave_OnClick(obj)
{
var ls_IsRemark = "";

	if (gfn_isNull(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "ABSENTDAY"))) {
        gfn_Alert("결강한 자료가 아닙니다.");
        return false;
    }


	if (!gfn_Confirm(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TJPERSONNM") 
	              + "님의 결강 면제처리를 하시겠습니까?")) {
		return false;
	              
	} else {
		// 결강면제 사유
		var parValue = "sTitle="+quote(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TJPERSONNM"));
			parValue += " sMeg="+quote(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TJPERSONNM") + "님의 결강 면제처리를 하시겠습니까?");
        ls_IsRemark = Dialog("training::frmtraining0043PAbsentMunjeRemark.xml", parValue, "", "", "", -1, -1);	
		
		if (ls_IsRemark == "" || ls_IsRemark = null) {
			return false;
		}
	
	}
	
	lfn_SaveModifyHistory("U"); // 변경이력을 저장한다.

    

    tit_clearActionInfo();

	tit_addSingleActionInfo("training:updateTraining0042AbsentMunje", "", 0, "");

	// 서버 호출 

	tit_callService(

		""

		,""

		,""	// inDs

		,""	// outDs

		,"TJMGNO=" + quote(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TJMGNO"))

			+ " REMARK=" + quote(ls_IsRemark)
				
			+ " TOMGNO=" + quote(ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TOMGNO")) // args 

		,"lfn_AfterSaveTrainingAbsentMunje"

		,false

	);

}

 

function lfn_AfterSaveTrainingAbsentMunje(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

    

    gfn_SaveMessage(); // 저장 메세지를 처리한다.

    lfn_Search(); // 부모창 그리드 Redraw.

}



/*

 * 기능 : 업무 변경이력을 저장한다.

 * parameter : sGubun - 변경구분. 예) I, U, D

 * return : 없음

 */

function lfn_SaveModifyHistory(sGubun) {    

    gds_iModifyHistory.InsertRow(0);                 // 데이터셋 row 추가            

    gds_iModifyHistory.SetColumn(0, "MHWORKGUBUN", "결강면제"); // 업무구분

    gds_iModifyHistory.SetColumn(0, "MHGUBUN", sGubun); // 변경구분

    gds_iModifyHistory.SetColumn(0, "MHKEY", ds_oTrainingAbsent.GetColumn(ds_oTrainingAbsent.currow, "TJMGNO")); // KEY값

    gds_iModifyHistory.SetColumn(0, "MHCOLUMN", "결강면제여부"); // 변경 컬럼명

    gds_iModifyHistory.SetColumn(0, "MHBEFOREDATA", ""); // 변경 전 값

    gds_iModifyHistory.SetColumn(0, "MHAFTERDATA", "Y"); // 변경 후 값        



    gfn_InsertModifyHistory(); // 업무 변경이력을 저장한다.

}
