function lfn_Search(obj)
{	
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
    // 강습교육출석부
    ds_oTrainingAttendence.ClearData(); // 데이타셋 초기화
    ds_oTrainingAttendencePoto.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchTrainingAttendence", false);
	tit_addSearchActionInfo("training:searchTrainingAttendencePoto", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingAttendence=ds_oTrainingAttendence ds_oTrainingAttendencePoto=ds_oTrainingAttendencePoto"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
            + " SORT=" + quote(radSort.Value)// args 
		,"lfn_AfterSearchTrainingAttendence"
		,false
		
		,false
		
		,true
	);	
}

function lfn_SearchJechul(obj)
{
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }    
	// 노동부제출용강습교육출석부
    ds_oTrainingAttendenceJechul.ClearData(); // 데이타셋 초기화
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTrainingAttendenceJechul", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingAttendenceJechul=ds_oTrainingAttendenceJechul"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)
            + " SORT=" + quote(radSort.Value)// args 
		,"lfn_AfterSearchTrainingAttendenceJechul"
		,false
	);
}

/*
 * 기능 : 출석부출력
 */
function lfn_Print(obj)
{    
    if (ds_oTrainingAttendencePoto.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "출석부출력"
        , ls_HistoryArg
    );
    
    var PrtTime = "";	
	// 12시 이전에는 발급일자만 출력
	// 12시 이후에는 발급일자+ 시간까지 출력
	
	if (substr(GetDate(), 8,4) > '1200') {
	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2)+" "+
					  substr(GetDate(), 8,2)+":"+substr(GetDate(), 10,2);
	
	} else {
	
		PrtTime = left(GetDate(), 4)+"-"+substr(GetDate(), 4,2)+"-"+substr(GetDate(), 6,2);
		
	}
	
	var sReportName = "";
	
	if (cbxCourse.Value == "10") {	// 특급소관
	
		sReportName = "frmtraining0120R02TrainingSpecialGradeAttendanceRegister.rex";
		
	} else if (cbxCourse.Value == "20") {	// 공공 
	
		sReportName = "frmtraining0120R01TrainingAttendanceRegister.rex";
	
	} else if ( cbxCourse.Value == "21" ) { // 23.07 (1급 5일--> 10일 )
		
		sReportName = "frmtraining0120R08TrainingAttendanceRegister.rex";

	}
	  // 직능2건 추가 관련하여 레포트 파일 별도 지정	
	  else if ( cbxCourse.Value == "30" ) {
		
		sReportName = "frmtraining0120R03TrainingAttendanceRegister.rex"; // 건설현장

	} else if ( cbxCourse.Value == "31" ) {
		
		sReportName = "frmtraining0120R04TrainingAttendanceRegister.rex"; // 업무대행자

	} else if (cbxCourse.Value == "22" ) {	// 23.07 (2급 4일 --> 5일) 
	
		sReportName = "frmtraining0120R01TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "23") {	// 3급소관
	
		sReportName = "frmtraining0120R03TrainingAttendanceRegister.rex";
		
	} else if (cbxCourse.Value == "24") {	// 위험물안전관리자
	
		sReportName = "frmtraining0120R03TrainingAttendanceRegister.rex";
		
	} else if (cbxCourse.Value == "27") {	// 위험물운반자
	
		sReportName = "frmtraining0120R05TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "28") {	// 위험물운송자
	
		sReportName = "frmtraining0120R04TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "29") {	// 위험물통합자
	
		sReportName = "frmtraining0120R02TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "50") {	// 생활안전강사
	
		sReportName = "frmtraining0120R03TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "51") {	// 심폐소생술
	
		sReportName = "frmtraining0120R05TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "52") {	// 교관양성과정
	
		sReportName = "frmtraining0120R01TrainingAttendanceRegister.rex";
	
	} else if (cbxCourse.Value == "53") {	// 심폐소생술전문자격 23.11 추가
	
		sReportName = "frmtraining0120R02TrainingAttendanceRegister.rex";
		
	}
	
    gfn_CallReportPopup(sReportName
                      , "ds_oTrainingAttendencePoto||ds_oTrainingAttendence"//||ds_oTrainingAttendencePoto
                      , "rpDirpath=" + GF_FILE_SERVER_URL + "kems"
                        
                        + "^PrtTime=" + PrtTime);
}

/*
 * 기능 : 노동부 제출용출석부출력
 */
function lfn_PrintJechul(obj)
{	
    if (ds_oTrainingAttendenceJechul.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text)
                + ",정렬방법=" + quote(radSort.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "노동부제출용출석부출력"
        , ls_HistoryArg
    );
    
    gfn_CallReportPopup("frmtraining0120R03LaborMinistrySubmitAttendanceBook.rex"
                      , "ds_oTrainingAttendenceJechul"
                      , "");
}

