/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 조회 Validation Check
        return false;
    }
    ds_oTrainingDayRegisterPrint.ClearData(); // 데이타셋 초기화
    
    // 조회이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text);
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTrainingDayRegisterPrint", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingDayRegisterPrint=ds_oTrainingDayRegisterPrint"	// outDs
		,"TOMGGTMGNO=" + quote(cbxJibu.Value)
            + " TOTCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " TOTRAININGORDER=" + quote(edTrainingOrder.Text)	// args 
		,"lfn_AfterSearchTrainingJubsu"
		,false
	);
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    ds_oTrainingDayRegisterTemp.ClearData();
    var allplusHour = 0;
    var groupValue = 0;   
    var nDay = 0; // 입력받은 날짜의 요일가져오는 함수
    var strDay = ""; // 요일 인덱스에 따라 지정한 요일 변수 설정        
    var eduDate = 0;
    var remain = 0;
    var temp = 0;
    for (i=0;i<ds_oTrainingDayRegisterPrint.GetRowCount();i++) {
        for (j=0;j<ToInteger(ds_oTrainingDayRegisterPrint.GetColumn(i, "LDEDUTIME"));j++) {
            temp = i+1+j+allplusHour;
            eduDate = ds_oTrainingDayRegisterPrint.GetColumn(i, "TOSTARTDATE");            
            nDay = getDay(ToInteger(eduDate)+groupValue); // 입력받은 날짜의 요일가져오는 함수
            strDay = decode( nDay, 0, "일", 1, "월", 2, "화", 3, "수", 4, "목", 5, "금", 6, "토" ); // 요일 인덱스에 따라 지정한 요일 변수 설정
            remain = decode(temp%8, 0, 8, temp%8);
            ds_oTrainingDayRegisterTemp.AddRow();
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "TODATE", (ToInteger(eduDate)+groupValue));
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "YOIL", strDay+"요일");            
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "LDEDUTIME", remain);
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "SJTITLE", ds_oTrainingDayRegisterPrint.GetColumn(i, "SJTITLE"));
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "LDPFNAME", ds_oTrainingDayRegisterPrint.GetColumn(i, "LDPFNAME"));
            ds_oTrainingDayRegisterTemp.SetColumn(temp-1, "TOTCCOURSENM", cbxCourse.Text);
            if ((i+1+j+allplusHour)%8 == 0) {
                groupValue = groupValue+1;
            }    
        } //end for j
        allplusHour = allplusHour+ToInteger(ds_oTrainingDayRegisterPrint.GetColumn(i, "LDEDUTIME"))-1;    
    }    
    lfn_Print();
}

/*
 * 기능 : 출력
 */
function lfn_Print(obj)
{
    if (ds_oTrainingDayRegisterPrint.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
    
    // 출력이력을 저장한다.  
    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)
                + ",직능=" + quote(cbxCourse.Text)
                + ",년도=" + quote(edTrainingYear.Text)
                + ",회차=" + quote(edTrainingOrder.Text);
    gfn_InsertSearchPrintHistory(
        "P"
        , this.ID
        , "강습접수자명단일괄출력"
        , ls_HistoryArg
    );
    
    // to do Validation Check
    gfn_CallReportPopup("frmtraining0140R01TrainingDayRegister.rex", "ds_oTrainingDayRegisterTemp", "");
}
