Window.Script:
/*
 * 프로그램명 : frmtraining0144PTrainingEvaluationAnswerView.xml
 * 설명 : 제출답안보기
 * 작성일 : 2021.02.04
 * 작성자 : 정현호
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sTjmgno")) {
        lfn_Search();
    }

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oEvaluationAnswer.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addSearchActionInfo("training:searchTraining0144EvaluationAnswerView", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oEvaluationAnswer=ds_oEvaluationAnswer" // outDs
        , "TJMGNO=" + quote(sTjmgno) // args
        , "lfn_AfterSearchEvaluationAnswerView"
        ,false
        ,false
        ,true
    );
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEvaluationAnswerView(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    gdEvaluation.SetCellProp("head", 1, "text", ds_oEvaluationAnswer.GetColumn(0, "SUBJECT1"));
    gdEvaluation.SetCellProp("head", 2, "text", ds_oEvaluationAnswer.GetColumn(0, "SUBJECT2"));
    gdEvaluation.SetCellProp("head", 3, "text", iif(ds_oEvaluationAnswer.GetColumn(0, "SUBJECT3") == NULL,"-",ds_oEvaluationAnswer.GetColumn(0, "SUBJECT3")));
    gdEvaluation.SetCellProp("head", 4, "text", iif(ds_oEvaluationAnswer.GetColumn(0, "SUBJECT4") == NULL,"-",ds_oEvaluationAnswer.GetColumn(0, "SUBJECT4")));
    gdEvaluation.SetCellProp("head", 5, "text", iif(ds_oEvaluationAnswer.GetColumn(0, "SUBJECT5") == NULL,"-",ds_oEvaluationAnswer.GetColumn(0, "SUBJECT5")));
    gdEvaluation.SetCellProp("head", 6, "text", iif(ds_oEvaluationAnswer.GetColumn(0, "SUBJECT6") == NULL,"-",ds_oEvaluationAnswer.GetColumn(0, "SUBJECT6")));
    gdEvaluation.SetCellProp("head", 7, "text", iif(ds_oEvaluationAnswer.GetColumn(0, "SUBJECT7") == NULL,"-",ds_oEvaluationAnswer.GetColumn(0, "SUBJECT7")));
    gfn_SearchMessage(gdEvaluation); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    if (nCell > 0) {
        var lobj_Dataset = object(obj.BindDataset);
        // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
        if (lobj_Dataset.GetRowCount() > 1) {
            gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
        }
    }
}

function btnMutilSort_OnClick(obj) {
	gfn_GridMultiSort(gdLcsInfo, 1); // 멀티 정렬을 실행한다.
}
