Window.Script:
/*
 * 교육접수정보명 : frmtraining0251PLicenseIssueHist.xml
 * 설명 : 수첩발급이력팝업
 * 작성일 : 2009.08.28
 * 작성자 : 김동현
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sLicenseNo")) {
        lfn_Search();
    }

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oLicenseH.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchLicenseH", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oLicenseH=ds_oLicenseH"	// outDs
		,"LHLLCSNO=" + quote(sLicenseNo)	// args 
		,"lfn_AfterSearchLcsInfo"
		,false
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */
/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchLcsInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdLcsHistory); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function gdLcsHistory_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
	btnLcsImage_OnClick(obj);
}

function btnLcsImage_OnClick(obj) {

	gfn_ImageViewPopup(
        ds_oLicenseH.GetColumn(ds_oLicenseH.currow,"LIDIRPATH")
        , ds_oLicenseH.GetColumn(ds_oLicenseH.currow,"LIREALNM")
        , ds_oLicenseH.GetColumn(ds_oLicenseH.currow,"LHLLCSNO")
    );
}

function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)
{
	
}
