Window.Script:
/*
 * 프로그램명 : frmtraining0254PLicensePrtCancel.xml
 * 설명 : 출력이력취소팝업
 * 작성일 : 2016.06.08
 * 작성자 : 정현호
 */

#include "LIB::COMMON.js"; // 공통 관련
var vCancelYn = "";
// sLPLLCSNO
// sLPLHSEQ
// sLPSEQ

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sLPLLCSNO")) {
        lfn_Search(obj);
    }
    edCanselRemark.Text = "출력 오류";
    edCanselRemark.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close(vCancelYn);
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 115 : // 저장(F4)
            lfn_Save();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    medLcsNo.Value = sLPLLCSNO;
    edGubun.Text = iif(sLPLHSEQ==1, "신규발급", "재발급");
    
    edPrtSEQ.Text = sLPSEQ;
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {

    if (!gfn_Confirm("출력내역을 취소 하시겠습니까?")) {
        return false;
    }

	// 출력내역 취소

	vCancelYn = "Y"; // 출력취소여부 확인 
	tit_clearActionInfo();
	tit_addSingleActionInfo("training:updateLicensePrtCancel", "", 0, "");
	// 서버 호출
	tit_callService(
		""
		,""
		,""    // inDs
		,""    // outDs
		,"LPLLCSNO=" + quote(sLPLLCSNO) // args
			+ " LPLHSEQ=" + quote(sLPLHSEQ)
			+ " LPSEQ=" + quote(sLPSEQ)
			+ " CANCELREASON=" + quote(edCanselRemark.Text)
		,"lfn_AfterSaveLicensePrtCancel"
		,false
		,false
		,true
	);


}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(vCancelYn);
}
/* 공통 필수 종료 */

/* 공통 함수 */

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveLicensePrtCancel() {

    Parent.lfn_searchLicensePrtHistory(sLPLLCSNO, sLPLHSEQ);
    lfn_End();
}

/* 공통 이벤트 함수 */
