Window.Script:
/*
 * 교육접수정보명 : frmtraining0271PPrizeAwardIssueList.xml
 * 설명 : 개인별상장발급이력팝업
 * 작성일 : 2009.08.10
 * 작성자 : 장성호
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
var ls_BIRTHDAY = "";
var ls_PersonNm = "";
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sBIRTHDAY")) {
        ls_BIRTHDAY = sBIRTHDAY;
        ls_PersonNm = sPersonNm;
        lfn_Search();
    }    
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        //case 120 : // 개인별상장발급이력(F9)
            //lfn_PrintPrizeList();
            //break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oPrize.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchPrizeIssueList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oPrize=ds_oPrize"	// outDs
		,"BIRTHDAY=" + quote(ls_BIRTHDAY)	// args 
            + " CUSTOMERNM=" + quote(ls_PersonNm)
		,"lfn_AfterSearchPrizeIssue"
		,false
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchPrizeIssue(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdPrizeIssue); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdPrizeIssue, 0); // 멀티 정렬을 실행한다.
}
