/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";



    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.



    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정

        lfn_SetFormAuth(true); // 저장 권한

    } else if (ms_FormAuth == "R") {

        lfn_SetFormAuth(false); // 읽기 권한

    } else {

        gfn_Alert(MSGI00080);

        close();

    }



    // 공통 코드 조회

    // 시험직능(E) / 수첩직능(L) / 강습직능(T)
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y"); // 직능 코드 조회
    ds_oCourse.InsertRow(0);
    ds_oCourse.SetColumn(0, "CD", "");
    ds_oCourse.SetColumn(0, "DATA", "전체조회");
    cbxCourse.Value = "22";
    // 전체를 추가함에따라 기본 전체로 선택되도록 설정하였음
    //cbxCourse.Index = 0;

    //gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회    
	gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회   
	cbxJibu.Index = 0;
    edTrainingYear.Text = left(GetDate(), 4);


	// 시스템관리자만 tjmgno, tpmgno가 보이도록 설정
	if (gds_oUserInfo.GetColumn(0, "ADMINGUBUN") == "SM") {
		btnPass.Visible  = true;
		btnPass2.Visible = true;
		btnPass3.Visible = true;
	} else {
		btnPass.Visible  = false;
		btnPass2.Visible = false;
		btnPass3.Visible = false;
	}

	// 콜센터 직원용 이면
	if (gs_IsCallCenter == "Y") {
		// 콜센터 Call ID 가져오기(Unique ID)
		global.mnuLeft.lfn_CallUIDImport();
	}

//gfn_Alert("수료증 출력에서 응시자, 합격자, 불합격자 조회 가능!!! 사유:개선의견 NO.30"); // 개발 전 알림용!!! 개발 후 삭제 바람.

}



