function Form_OnSize(obj,nCx,nCy,nState)

{

    if (gs_IsResizingForm == "Y") {

        gdTrainingPass.Height = nCy - gdTrainingPass.Top - 4;

        gdTrainingPass.Width = nCx - gdTrainingPass.Left - 4;

    }

}



/*

 * 기능 : 조회

 */

function lfn_Search(obj) {

	/*
    if (!gfn_FormValidate(this)) { // 입력 Validation Check

        return false;

    }*/
    
    if(edTrainingYear.Text == "" || edTrainingYear.Text = null)
    {
		gfn_Alert(" 년도를 입력하세요!.");
		return;
    }
   

    ds_ioTrainingPass.ClearData(); // 데이타셋 초기화

    ds_ioReport2.ClearData();      // 레포트 최종 전달 데이터셋 초기화

    

    

    var PassNum ="";  // 수료연번

    PassNum =   edTrainingYear.text//  -- 년도

                +"-"+ mid(cbxJibu.value, 1,2)//  -- 지부    

                +"-"+ cbxCourse.value//  --  직능

                +"-1-";

    

    SearchWhere ="TOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부

                 +" TOYEAR=" + quote(edTrainingYear.text)  //-- 년도

                 +" TOTCCOURSECD=" + quote(cbxCourse.value) // -- 직능
                 
                 +" TOONLINEGUBUN=" + quote(cbxOnlineGubun.Value); // -- 실시간온라인 구분  ( 추가 23.04.11 ) 

                // 리디오 버튼 선택에 따른 검색조건 개체를 사용하기 위함

   

    switch (radSearchGubun.Value) {

        case "01": //                    

            SearchWhere = SearchWhere + " TOTRAININGORDER=" + quote(divSearchGubun.edTrainingOrder.text) // -- 수료회차

                                      + " TOHJUBSUNO=" + quote(divSearchGubun.edJubsuSeqStart.text) // -- 접수번호1

                                      + " TOHJUBSUNO2=" + quote(divSearchGubun.edJubsuSeqEnd.text); // -- 접수번호2                                     

            break;

        case "02": //                     

            SearchWhere = SearchWhere + " TPPASSNO=" + quote(divSearchGubun.edPassSeqStart.text)//  -- 수료연번1                                      

                                      + " TPPASSNO2=" + quote(divSearchGubun.edPassSeqEnd.text); // -- 수료연번2                                      

            break;

        case "03": //                      

            SearchWhere = SearchWhere + " TPPASSDATE=" + quote(divSearchGubun.calDateStart.value)//  -- 수료일자1

                                      + " TPPASSDATE2=" + quote(divSearchGubun.calDateEnd.value); // -- 수료일자2                                      

             ;

            break;

        case "04": //                     

            SearchWhere = SearchWhere + " TJBIRTHDAY=" + quote(divSearchGubun.medBIRTHDAY.value);//  -- 생년월일 ;                                      

            break;

        case "05": //                    

            SearchWhere = SearchWhere + " TOEXAMORDER=" + quote(divSearchGubun.edTrainingOrder2.text)  //-- 시험회차

            +" ERPASSYN=" + quote(divSearchGubun.radExamPassYn.value);  //-- 합격여부                                                           

             break;

        case "06": //                    

            SearchWhere = SearchWhere + " TJPERSONNM=" + quote(divSearchGubun.edTrainingPnm.text);  //-- 성명                                                

             break;

                 

         }

    ////////////////////  조회이력 남기기

    ls_HistoryArg = SearchWhere ;          

    // 조회 로그

    gfn_InsertSearchPrintHistory(

        "S"

        , this.ID

        , global.mnuLeft.cbxOpenForm.Text

        , ls_HistoryArg

    );      
    
	// 콜센터 직원용 이면
	if (gs_IsCallCenter == "Y") {
		// 콜센터 직원 조회이력 저장
		gfn_InsertCallCenterSearchPrintHistory(
		
				"S"
				
				, gs_CallCenterUID
	
				, gds_OpenedForm.GetColumn(gds_OpenedForm.FindRow("MURL",this.ID),"MMENUID")

				, this.ID
	
				, global.mnuLeft.cbxOpenForm.Text
	
				, ls_HistoryArg
				
			);
	}

    ////////////////////     

    tit_clearActionInfo();
	var str = "";
	str = "training:searchTrainingPass";
	
	
	tit_addSearchActionInfo(str, false);

	// 서버 호출 

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_ioTrainingPass=ds_oTrainingPass"	// outDs

		,SearchWhere 	// args  // 리디오 버튼 선택에 따른 검색조건 개체를 사용하기 위함

		,"lfn_AfterSearchTrainingPass"

		,false

		

	);

}

/* 공통 필수 종료 */



/* 사용자 함수 */

/*

 * 기능 : 결강자 조회 후 처리해야 할 내용

 * parameter : nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSearchTrainingPass(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }

   
    gfn_SearchMessage(gdTrainingPass); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    medAbsenCnt.Text = ds_ioTrainingPass.count();  // 수료인원을 보여준다.

}



/*

 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용

 * parameter : strSvcID - 서비스ID

 *             nErrorCode - 에러코드

 *             strErrorMsg - 에러메시지

 * Return : 없음

 */

function lfn_AfterSaveTrainingPass(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.

        gfn_ErrorMsg(nErrorCode, strErrorMsg);

        return;

    }



    gfn_SaveMessage(); // 저장 메세지를 처리한다.

    lfn_Search(); //  조회

}



/*

 * 기능 : 버튼 활성/비활성시킨다.

 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)

 * return : 없음

 */

function lfn_SetFormAuth(bStatus) {

    // 콜센터 직원 사용 이면
	if (gs_IsCallCenter == "Y") {
		btnToExcel.Enable = false; // 엑셀출력 버튼
		btnPrintScreen.Enable = false; // 화면출력 버튼
		btnPassList.Enable = false; // 수료대장 버튼
	}

}



