function gdTraining_OnHeadClick(obj,nCell,nX,nY,nPivotIndex)

{

	var lobj_Dataset = object(obj.BindDataset);

	if (nCell == 0) { // 선택

        if (obj.GetCellProp("head", nCell, "text") == 1) { // 전체가 이미 선택된 경우

            obj.SetCellProp("head", nCell, "text", 0);

            for(i = 0; i < lobj_Dataset.RowCount(); i++) {

                lobj_Dataset.SetColumn(i, "SEL", "0");

            }

        } else { // 전체가 이미 선택되지 않은 경우

            obj.SetCellProp("head", nCell, "text", 1);

            for(i = 0; i < lobj_Dataset.RowCount(); i++) {

                lobj_Dataset.SetColumn(i, "SEL", "1");

            }

        }

	} else { // 헤드 정렬

        lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex);

	}

}



function btnPassPrint()

{

	if(!gfn_CheckChangedGrid(this))  //선택한것이 있는지 체크 메세지 보임

        { alert("출력할 인원을 선택해 주십시오.");      

            return false;

        }

    ds_ioReport.copy(ds_ioTrainingPass);  // 1) 원본 복사           

    ds_ioReport.Filter("SEL = '1'"); // 2) 복사 후 필터함         

    ds_ioReport2.copyf(ds_ioReport);  // 3) 필터한 값을 실제 사용 위해 다른 곳에 복사     

    ds_ioReport.ClearData(); // 4) 데이타셋 초기화  레포트 출력위해 필터된 2)번의 데이터셋을 초기화함

 

 //   gfn_CallReportPopup("frmtraining0280R01DiplomaWarrant.rex", "ds_ioReport2", "SEL = 1") ;

    

    if (!Confirm("총" + ds_ioReport2.count() + "건의 수료증이 출력되고 출력기록이 보관됩니다. 출력 하시겠습니까? ")) { //  출력전 확인 메시지

        return false;

	}

    

    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)

            + ",직능=" + quote(cbxCourse.Text)

            + ",년도=" + quote(edTrainingYear.Text);

          //  + ",회차=" + quote(edTrainingOrder.Text);

  

    gfn_InsertSearchPrintHistory(

        "P"

        , this.ID

        , global.mnuLeft.cbxOpenForm.Text

        , ls_HistoryArg

    ); 


	// 콜센터 직원용 이면
	if (gs_IsCallCenter == "Y") {
		// 콜센터 직원 조회이력 저장
		gfn_InsertCallCenterSearchPrintHistory(
		
				"P"
				
				, gs_CallCenterUID
	
				, gds_OpenedForm.GetColumn(gds_OpenedForm.FindRow("MURL",this.ID),"MMENUID")

				, this.ID
	
				, global.mnuLeft.cbxOpenForm.Text+"(수료증)"
	
				, ls_HistoryArg
				
			);
	}  

    // 바로 출력하기

    if (cbxCourse.Value < "50") {    	// 생활안전강사가 아니면

		gfn_PrintReport("frmtraining0280R01DiplomaWarrant.rex", "ds_ioReport2", "","true","", false) ;
		
	} else if (cbxCourse.Value >= "50" && cbxCourse.Value <= "51") {  	// 생활안전강사, 심폐소생술 이면
	
		gfn_PrintReport("frmtraining0280R06LifeSafetyDiplomaWarrant.rex", "ds_ioReport2", "SEL = 1") ;
	
	} else if (cbxCourse.Value == "53") {  	//  심폐소생술전문자격 이면 23.11 
	
		gfn_PrintReport("frmtraining0280R06LifeSafetyDiplomaWarrant.rex", "ds_ioReport2", "SEL = 1") ;

	} else if (cbxCourse.Value == "52") {  	//  교관양성과정 이면
	
		gfn_CallReportPopup("frmtraining0280R09LifeSafetyDiplomaWarrant.rex", "ds_ioReport2", "SEL = 1" +"^rpDirpath=" + GF_FILE_SERVER_URL + "kems") ;

	}


    

    for(i = 0; i < ds_ioReport2.RowCount(); i++) {

        if (ds_ioReport2.GetColumn(i, "SEL") == 1) {

           

           ds_ioReport2.SetColumn(i, "SEL", "0");

           

        } 

    } 

    // 데이터 셋 모두 SEL 셀 변경함 

    // 이력 남기기

        

    tit_clearActionInfo();

    tit_AddMultiActionInfo(	

        "training:insertPassCardList"	// insert  		

        , ""	// update  

        , ""	// delete

        , ""	// flag column 명

        , ""	// key sql id

        , 0	// key 증가 값

        , "" 

        , 0 

        , "N"	// 실행타입

    );

    // 서버 호출 

    tit_callService(

        ""

        ,""

        ,"ds_iPassCardList=ds_ioReport2:U"/// inDs		

        ,""	// outDs

        ,"PDEPTCD=" + gds_oUserInfo.GetColumn(0, "PDEPTCD") // -- 지부 

           +" PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") // -- 사번 

       

        // args 

        ,"lfn_AfterSearchTrainingPass"

        ,false

    ); 

       

 

}





function lfn_localExcel(obj)

{

	 if (ds_ioTrainingPass.GetRowCount() < 1) {

        gfn_Alert("출력할 데이터가 존재하지 않습니다.");

        return false;

    }

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1115";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioTrainingPass.GetRowCount();
		parValue += " sTitle=" + quote(gdTrainingPass.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);

}


