#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회

    calDateStart.Value = GetDate();
    calDateEnd.Value = GetDate();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdTrainingJubsu.Height = nCy - gdTrainingJubsu.Top - 4;
        gdTrainingJubsu.Width = nCx - gdTrainingJubsu.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oTrainingJubsu.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchTrainingJubsuStat", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingJubsu=ds_oTrainingJubsu"	// outDs
		,"TOMGJIBU=" + quote(cbxJibu.Value)
            + " TCCOURSECD=" + quote(cbxCourse.Value)
            + " TOYEAR=" + quote(edTrainingYear.Text)
            + " THORDERSEQSTART=" + quote(edTrainingOrderStart.Text)
            + " THORDERSEQEND=" + quote(edTrainingOrderEnd.Text)
            + " TJREPAYYN=" + quote(radRepayYn.Value)
            + " TJPASSYN=" + quote(radPassYn.Value)	// args 
		,"lfn_AfterSearchTrainingJubsu"
		,false
	);
}

/*
 * 기능 : 출력
 */
function lfn_Print(obj)
{
    // to do Validation Check
    gfn_CallReportPopup("TestRexpert.rex", "ds_oTrainingJubsu", "TOMGJIBU=" + quote(cbxJibu.Value)
                                                      + " TCCOURSECD=" + quote(cbxCourse.Value)
                                                      + " TOYEAR=" + quote(edTrainingYear.Text)
                                                      + " THORDERSEQSTART=" + quote(edTrainingOrderStart.Text)
                                                      + " THORDERSEQEND=" + quote(edTrainingOrderEnd.Text)
                                                      + " TJREPAYYN=" + quote(radRepayYn.Value)
                                                      + " TJPASSYN=" + quote(radPassYn.Value));
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdTrainingJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveTrainingJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 결강자 조회
}

