 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {

}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdTrainingJubsu, 0); // 멀티 정렬을 실행한다.
}

/* 회차 포커스 아웃될때 자동 0 입력 */
function edTrainingOrder_OnKillFocus(obj)
{
	gfn_ComponentOnKillFocus(obj);

    if (obj.Text != "") {
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));
    }
}

function ds_oTrainingJubsu_OnRowPosChanged(obj,nOldRow,nRow)
{
    // 그리드의 값을 입력란에 복사한다. 
	cbxJibu.Value = obj.GetColumn(nRow, "TOMGJIBU");
    cbxCourse.Value = obj.GetColumn(nRow, "TCCOURSECD");
    edTrainingYear.Text = obj.GetColumn(nRow, "TOYEAR");
    edTrainingOrderStart.Text = obj.GetColumn(nRow, "THORDERSEQSTART");
    edTrainingOrderEnd.Text = obj.GetColumn(nRow, "THORDERSEQEND");
    radRepayYn.Value = obj.GetColumn(nRow, "TJREPAYYN");
    radPassYn.Value = obj.GetColumn(nRow, "TJPASSYN");
}
