#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회
    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회

    calDateStart.Value = AddDate(GetDate(), -1);
    calDateEnd.Value = AddDate(GetDate(), -1);
   

}

function Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objJubsuerObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdElecAppr.Height = nCy - gdElecAppr.Top - 10;
        gdElecAppr.Width = nCx - gdElecAppr.Left - 8;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    ds_ioElecApprLicenseConnection.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchElecApprLicenseConnection", false);
	// 서버 호출 
	
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioElecApprLicenseConnection=ds_oElecApprLicenseConnection"	// outDs
		,"JIBU=" + quote(cbxJibu.value) // 지부 	// args 
		,"lfn_AfterSearchElecApprLicense"
		,false
	);
}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchElecApprLicense(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdElecAppr); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

};




/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnCreateList.Enable = bStatus;
    btnProcElecApv.Enable = bStatus;
}

/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdElecAppr, 0); // 멀티 정렬을 실행한다.
}

function btnCreateList_OnClick(obj)
{	
	ds_ioLicenseList_A.ClearData(); // 데이터셋 초기화
    tit_clearActionInfo();
    tit_AddCsvSearchActionInfo("training:searchElecApprLicenseList_A", false);
    tit_callService(
        ""
        ,""
        ,""// inDs
        ,"ds_ioLicenseList_A=ds_oLicenseList"	// outDs
        ,"JIBU=" + quote(cbxJibu.value)
            + " STARTDATE=" + quote(calDateStart.Value)
            + " ENDDATE=" + quote(calDateEnd.Value)	// args 
        ,"lfn_AfterSearchElecApprLicenseList"
        ,false
        ,""
        ,true
    );

}
function lfn_AfterSearchElecApprLicenseList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

   // gfn_SearchMessage(gdElecAppr); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}
function btnProcElecApv_OnClick(obj)
{
   btnProcElecApv.Enable = false;
   if(!Confirm("전자결재 연동 처리를 하시겠습니까?")){  //선택한것이 있는지 체크 메세지 보임
        return false;
    }
    
	ds_ioLicenseList_B.ClearData(); // 데이터셋 초기화


    // 연동 자료 조회하기 
    tit_clearActionInfo();
    tit_addSearchActionInfo("training:searchElecApprLicenseList_B", false);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioLicenseList_B=ds_oLicenseList"	// outDs
		,"JIBU=" + quote(cbxJibu.Value)
		    +" PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") // -- 사번 
            + " STARTDATE=" + quote(calDateStart.Value)  // 처리일자1
            + " ENDDATE=" + quote(calDateEnd.Value)	// 처리일자2 args 
		,"lfn_AfterSearchElecApprLicenseList"
		,false
		,""
		,true
				
	);

	// 연동 시스템에 자료 넣기
	tit_clearActionInfo();
	tit_AddSingleActionInfo("training:deleteElecApprMISLICENSE", "", 0, "");	// 기존 가비지 데이터 삭제 EKPDEV.MISLICENSE
    tit_AddSingleActionInfo(	
        "training:insertElecApprLicenseList"	// insert  		
        , ""	// update  
        , ""	// delete
        , ""	// flag column 명
        , ""	// key sql id
        , 0	// key 증가 값
        , "" 
        , 0 
        , "N"	// 실행타입
    );
    // 서버 호출 
    tit_callService(
        ""
        ,""
        ,"ds_ioLicenseList=ds_ioLicenseList_B"/// inDs		
        ,""	// outDs
        ,"PSABUN=" + gds_oUserInfo.GetColumn(0, "PSABUN") // -- 사번  
        
            + " STARTDATE=" + quote(calDateStart.Value)  // 처리일자1
            + " ENDDATE=" + quote(calDateEnd.Value)	// 처리일자2  
            
            + " JIBU=" + quote(cbxJibu.Value)	// 지부 
            
            // args 
        ,"lfn_AfterSaveElecAppr"
        ,false
    ); 
}
/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveElecAppr(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	ds_oGYMGNO.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchElecApprLicenseGYMGNO", false);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oGYMGNO=ds_oGYMGNO"	// outDs
		,"JIBU=" + quote(cbxJibu.value) // -- 지부 
		
			+ " PSABUN=" + quote(gds_oUserInfo.GetColumn(0, "PSABUN")) // -- 사번 // args 
		,""
		,false
		
		,""
		
		,true
	);
	
    lfn_Search(); // 조회
    btnProcElecApv.Enable = true;
    ExecBrowser("http://172.19.1.1/ekp/mis_index.jsp?uid=" + gds_oUserInfo.GetColumn(0, "PPERSONNM") + 
                                           "&employeeNumber=" + gds_oUserInfo.GetColumn(0, "PSABUN") +
                                           "&GYMGNO=" + ds_oGYMGNO.GetColumn(0, "LMGNO") + "&MTSTYPE=29");

}

function lfn_localExcel(obj)
{  
    if (ds_ioLicenseList_A.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	lfn_Excel(obj);
}


