{
		if(!gfn_isNull(ds_ioElecApprLicenseConnection.GetColumn(nRow, "RE_ALI_DOC_ID"))) {
	
			// 위촉결재문서 보기
			ExecBrowser("http://172.19.1.1/ekp/mis_index.jsp?uid=" + gds_oUserInfo.GetColumn(0, "PPERSONNM") + 		
												   "&employeeNumber=" + gds_oUserInfo.GetColumn(0, "PSABUN") +		
												   "&GYMGNO=" + ds_ioElecApprLicenseConnection.GetColumn(nRow, "RE_ALI_DOC_ID") + "&MTSTYPE=10");
												   		}


}
