/* 사용자 이벤트 함수 */
function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdInternetReIssue, 0); // 멀티 정렬을 실행한다.
}

function btnSendMailLink_OnClick(obj)
{
    gs_SystemGubun = "training";
    gs_SystemMenuId = "0000000003";
    global.mnuLeft.tabMenu.tabpgMenu.Text = "강습관리";
    var ls_MenuId = "0000000019";
    var ls_MenuTitle = "인터넷수첩재발급신청메일발송관리";
    var ls_MenuUrl = "training::frmtraining0700MEmailSendManagement.xml";

    global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
	gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId);
}

function btnInternetReIssueStatLink_OnClick(obj)
{
	if (radWorkGubun.Value == "TRAINING") { // 교육수첩재발급신청
        gs_SystemGubun = "training";
        gs_SystemMenuId = "0000000003";
        global.mnuLeft.tabMenu.tabpgMenu.Text = "강습관리";
        var ls_MenuId = "0000000045";
        var ls_MenuTitle = "인터넷강습수첩재발급신청현황";
        var ls_MenuUrl = "training::frmtraining0060MInternetTrainingReIssueStatistics.xml";
    
        global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
        gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId);
	} else { // 시험수첩재발급신청
        gs_SystemGubun = "training";
        gs_SystemMenuId = "0000000003";
        global.mnuLeft.tabMenu.tabpgMenu.Text = "강습관리";
        var ls_MenuId = "0000000026";
        var ls_MenuTitle = "인터넷시험수첩재발급신청현황";
        var ls_MenuUrl = "training::frmtraining0070MInternetExamReIssueStatistics.xml";
    
        global.mnuTop.tabTopMenu.TabIndex = gds_oTopMenu.FindRow("MSYSGUBUN", gs_SystemGubun);
        gfn_OpenChildWindow(ls_MenuUrl, ls_MenuTitle, ls_MenuId);
	}	
}

function radWorkGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "TRAINING") { // 교육수첩재발급신청
        gdInternetReIssue.Format = "Training";
	} else { // 시험수첩재발급신청
        gdInternetReIssue.Format = "Exam";
	}
}
