#include "LIB::training.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

    // 공통 코드 조회

    calDateStart.Value = "19700101";
    calDateEnd.Value = GetDate();    
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do

    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdTrainingJubsu.Height = nCy - gdTrainingJubsu.Top - 4;
        gdTrainingJubsu.Width = nCx - gdTrainingJubsu.Left - 4;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_ioLicenseList.ClearData(); // 데이타셋 초기화

    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    
    if (gfn_isNull(edSiGunGu.Text)) {
    
		gfn_Alert("시군구를 입력하세요!");
		
		return false;
    
    }
    
    
    
    ///////////////////// 조회이력 남기기
    ls_HistoryArg = "자격증" + quote(radCourse.Text)   // 직능
    
			+ " 발급일자1=" + quote(calDateStart.Value)   // 발급일자1
            + " 발급일자2=" + quote(calDateEnd.Value)	// 발급일자2
            
            + " 시군구=" + quote(edSiGunGu.Text);	// 시군구
            

        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
        ); 
    /////////////////////      
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchTraining0386LicenseSiGunGuList", false);
	// 서버 호출 	
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioLicenseList=ds_oLicenseList"	// outDs
		,"COURSE=" + quote(radCourse.Value)   // 직능
		
			+ " LHPRTDATE=" + quote(calDateStart.Value)   // 발급일자1
            + " LHPRTDATE2=" + quote(calDateEnd.Value)	    // 발급일자2  
            
            + " SIGUNGU=" + quote(edSiGunGu.Text)	    // 시군구  
            
		,"lfn_AfterSearchLicenseList"
		,false
	);
}

/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchLicenseList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
    medLicenseCnt.Value = ds_ioLicenseList.count;  // 조회건수 표기함
    
    gfn_SearchMessage(gdTrainingJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
   
}


