}

/* 회차 포커스 아웃될때 자동 0 입력 */
function edTrainingOrder_OnKillFocus(obj)
{
	gfn_ComponentOnKillFocus(obj);

    if (obj.Text != "") {
        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));
    }
}



function lfn_localExcel(obj)
{  
    if (ds_ioLicenseList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return false;
    }
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1305";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_ioLicenseList.GetRowCount();
		parValue += " sTitle=" + quote(gdLicenseList.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}


function Form_OnActivate(obj)
{
	
}

function edSiGunGu_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
    switch (nChar) {
        case 13 : // 조회(F2)
            lfn_Search();
            break;

        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
            
        case 123 : // 화면출력(F12)
            if (obj.btnPrintScreen.Enable) {
                obj.lfn_PrintScreen();
            }
            break;
    }
}
