Window.Script:
/*
 * 시험접수정보명 : frmtraining0401PExamJubsuList.xml
 * 설명 : 시험접수검색팝업
 * 작성일 : 2009.09.16
 * 작성자 : 이규희
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    gfn_SearchTrainingCourse("E", ds_oCourse, "Y", ""); // 직능 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회       
    edExamOrder.Text = sOrder;
    edExamYear.Text = sYear;
    cbxJibu.Value = sGtMgno;
    cbxCourse.Value = sCourse;
    
    lfn_Search();

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oExamJubsu.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("training:searchExamJubsuList2", false);
	
	var sArg  =  "EOMGNO=" + quote(eomgno);
        sArg +=  " EOHJUBSUNO=" + quote(eohjubsuno);
        //sArg +=  " sLikeYn=" + quote(slikeyn);

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oExamJubsu=ds_oExamJubsuList"	// outDs
		,sArg
		,"lfn_AfterSearchExamJubsu"
		,false
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */
/* 시험접수 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchExamJubsu(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdExamJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 시험접수 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdExamJubsu, 0); // 멀티 정렬을 실행한다.
}

function btnSelect_OnClick(obj)
{
	if (ds_oExamJubsu.GetCount() > 0) {
        
        parent.ds_ioExamJubsu.ClearData(); // 데이타셋 초기화
        parent.ds_ioExamJubsu.InsertRow(0);
        
        ds_oExamJubsu.CopyToRow(ds_oExamJubsu.currow, parent.ds_ioExamJubsu, 0);
        parent.ds_ioExamJubsu.ApplyChange();
        Close("OK");
    }
}

function gdExamJubsu_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
    btnSelect_OnClick(obj);
}

function edExamJubsu_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        lfn_Search();
	}
}

function gfn_ComponentOnKillFocus(obj)
{
	
}
