/* 공통 필수 시작 */    

function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리



    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";



    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.



    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.

    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정

        lfn_SetFormAuth(true); // 저장 권한

    } else if (ms_FormAuth == "R") {

        lfn_SetFormAuth(false); // 읽기 권한

    } else {

        gfn_Alert(MSGI00080);

        close();

    }


    // 공통 코드 조회
    
	// 시험직능(E) / 수첩직능(L) / 강습직능(T)
	gfn_SearchTrainingCourse("E", ds_oCourse, "Y", "전체"); // 직능(E) 코드 조회

    gfn_SearchJibu(cbxJibu, "Y", ""); // 지부 코드 조회  
    
    
    if(gds_oUserInfo.GetColumn(0, "PDEPTCD") == "0253"
     || gds_oUserInfo.GetColumn(0, "PSABUN") == "20220952"
     || gds_oUserInfo.GetColumn(0, "PSABUN") == "20220951")// 평가관리과는 전체권한 적용
    {
		ds_oJibu.InsertRow(0);
		ds_oJibu.SetColumn(0, "CD", "");
		ds_oJibu.SetColumn(0, "DATA", "전체");
		cbxJibu.Index = 0;
	}

	// 시험일정관리에서 더블클릭하여 조회된 자료이면
	if (!gfn_isNull(sJibu)) {

		cbxJibu.Value = sJibu;
		
		edExamYear.Text = sYear;
		
		cbxCourse.Value = sCourse;
		
		edExamOrderStart.Text = sOrder;
		
		edExamOrderEnd.Text = sOrder;
		
		radRepayYn.Value = sSunapGubun;
	
		lfn_Search(obj);
	
	} else {

		cbxCourse.Value = "22";
	
		edExamYear.Text = left(GetDate(), 4);
	
		edExamOrderStart.Text = "001";
	
		edExamOrderEnd.Text = "001";
	
		edExamOrderStart.SetFocus();

	}
	
	chkall.Value = 1;
	
	/// 특급일때만 사용하기 때문에 visible 처리 
	Static1.Visible = false;
	radJubsugubun2.Visible = false;
	radJubsugubun2.Index = 0;
	
}



function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {

    // to do



    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리

}



function Form_OnSize(obj,nCx,nCy,nState) {

    if (gs_IsResizingForm == "Y") {

        gdExamJubsu.Height = nCy - gdExamJubsu.Top - 4;

        gdExamJubsu.Width = nCx - gdExamJubsu.Left - 4;

    }

}



/*

 * 기능 : 조회

 */

function lfn_Search(obj) {

    /*if (!gfn_FormValidate(this)) { // 입력 Validation Check

        return false;

    }*/
    
    if(chkall.Value == 0 && chk1.Value == 0 && chk2.Value == 0 && chk3.Value == 0 && chk4.Value == 0 && chk5.Value == 0 && chk6.Value == 0)
    {
		chkall.Value = 1;
    }
    
    ds_oExamJubsu.ClearData(); // 데이타셋 초기화

    // 조회이력을 저장한다.  

    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)

                + ",직능=" + quote(cbxCourse.Text)

                + ",년도=" + quote(edExamYear.Text)

                + ",회차시작=" + quote(edExamOrderStart.Text)

                + ",회차종료=" + quote(edExamOrderEnd.Text)

                + ",환불여부=" + quote(radRepayYn.Text)

                + ",합격여부=" + quote(radPassYn.Text)
                
                + ",응시자격=" + quote(radJubsuGubun.Text)
                
                + ",가접수여부=" + quote(radJubsugubun2.Value);

    gfn_InsertSearchPrintHistory(

        "S"

        , this.ID

        , global.mnuLeft.cbxOpenForm.Text

        , ls_HistoryArg

    );

    

    medJubsuCnt.Value = "";

    tit_clearActionInfo();

	tit_addCsvSearchActionInfo("training:searchExamJubsuStat", false);

	// 서버 호출 

	tit_callService(

		""

		,""

		,""	// inDs

		,"ds_oExamJubsu=ds_oExamJubsu"	// outDs

		,"EOMGGTMGNO=" + quote(cbxJibu.Value)

            + " EOTCCOURSECD=" + quote(cbxCourse.Value)

            + " EOYEAR=" + quote(edExamYear.Text)

            + " EOEXAMORDERSTART=" + quote(edExamOrderStart.Text)

            + " EOEXAMORDEREND=" + quote(edExamOrderEnd.Text)

            + " EJLASTSUNAPGUBUN=" + quote(radRepayYn.Value)

            + " EJPASSYN=" + quote(radPassYn.Value)
            	
            + " EJJUBSUGUBUN=" + quote(radJubsuGubun.Value)	
            
            + " EJJUBSUGUBUN2=" + quote(radJubsugubun2.Value)// args 
            
            + " EXAMSTARTDATE=" + quote(calExamStart.Value)
            
            + " EXAMENDDATE=" + quote(calExamend.Value)
            
            ////////////////////////////////////////////////////
            //2024.01.04 응시자격 다중선택조회 요청으로 기존 radio를 ==> 각각 checkbox로 변경하여
            //다중 선택조회가 가능하도록 하였음
            + " CHKALL=" + quote(chkall.Value)
            + " CHK1=" + quote(chk1.Value)  // 강습
            + " CHK2=" + quote(chk2.Value)  // 경력
            + " CHK3=" + quote(chk3.Value)  // 학력
            + " CHK4=" + quote(chk4.Value)  // 학경력
            + " CHK5=" + quote(chk5.Value)  // 자격경력
            + " CHK6=" + quote(chk6.Value)  // 자격

		,"lfn_AfterSearchExamJubsu"

		,false

	);
}
	



/*

 * 기능 : 접수자 목록출력

 */

function lfn_Print(obj) {

    // to do Validation Check

    if (ds_oExamJubsu.RowCount() < 1) {

        gfn_Alert("출력할 데이터가 없습니다.");

        return false;

    }

    // 출력이력을 저장한다.  

    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)

                + ",직능=" + quote(cbxCourse.Text)

                + ",년도=" + quote(edExamYear.Text)

                + ",회차시작=" + quote(edExamOrderStart.Text)

                + ",회차종료=" + quote(edExamOrderEnd.Text)

                + ",환불여부=" + quote(radRepayYn.Text)

                + ",합격여부=" + quote(radPassYn.Text)
                
                + ",응시자격=" + quote(radJubsuGubun.Text);

    gfn_InsertSearchPrintHistory(

        "P"

        , this.ID

        , "접수자목록출력"

        , ls_HistoryArg

    );

    

    ds_oExamJubsu.Sort("EOEXAMORDER:A EOHJUBSUNO:A");

    gfn_CallReportPopup("frmtraining0405R01ExamJubsuList.rex", "ds_oExamJubsu", "");

}



/*

 * 기능 : 응시원서 일괄출력

 */

function lfn_PrintAll(obj)

{

	// to do Validation Check

	if (ds_oExamJubsu.RowCount() < 1) {

        gfn_Alert("출력할 데이터가 없습니다.");

        return false;

    }
    
    var ls_PrintGubun = "";
	// 23.07 출력구분에 따라 출력물에서 개인정보 삭제 
	if (obj.ID == "btnPrintAllEx") {
	
		ls_PrintGubun = "^rpPrivacy='Y'";
	}

    // 출력이력을 저장한다.  

    ls_HistoryArg = "지부=" + quote(cbxJibu.Text)

                + ",직능=" + quote(cbxCourse.Text)

                + ",년도=" + quote(edExamYear.Text)

                + ",회차시작=" + quote(edExamOrderStart.Text)

                + ",회차종료=" + quote(edExamOrderEnd.Text)

                + ",환불여부=" + quote(radRepayYn.Text)

                + ",합격여부=" + quote(radPassYn.Text)
                
                + ",응시자격=" + quote(radJubsuGubun.Text);

    gfn_InsertSearchPrintHistory(

        "P"

        , this.ID

        , "응시원서일괄출력"

        , ls_HistoryArg

    );


    ds_oExamJubsu.Sort("EOHJUBSUNO:A");

    if (cbxCourse.Value < "50") {

		gfn_CallReportPopup("frmtraining0400R01ExamJubsu.rex", "ds_oExamJubsu", "rpDirpath=" + GF_FILE_SERVER_URL + "kems" + ls_PrintGubun);
		
	} else if (cbxCourse.Value >= "50") {
	
		if (cbxCourse.Value == "53") { // 23.11 심폐소생술전문자격
			gfn_CallReportPopup("frmtraining0400R03ExamJubsu.rex", "ds_oExamJubsu", "rpDirpath=" + GF_FILE_SERVER_URL + "kems" + ls_PrintGubun);
		}else{
			gfn_CallReportPopup("frmtraining0400R02ExamJubsu.rex", "ds_oExamJubsu", "rpDirpath=" + GF_FILE_SERVER_URL + "kems" + ls_PrintGubun);		
		}
	
	}
	

}

