/* 회차 포커스 아웃될때 자동 0 입력 */

function edExamOrder_OnKillFocus(obj) {

	gfn_ComponentOnKillFocus(obj);



    if (obj.Text != "") {

        obj.Text = iif(gfn_IsNull(obj.Text), "", lpad(obj.Text, "0", 3));

    }

}



function edExamOrderEnd_EnterSearch(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)

{

	if (nChar == 13) { // Enter Key

        edExamOrderEnd.Text = iif(gfn_IsNull(edExamOrderEnd.Text), "", lpad(edExamOrderEnd.Text, "0", 3));

        lfn_Search();

    }

}



function btnToExcel_OnClick(obj) {

    if (ds_oExamJubsu.GetRowCount() < 1) {

        gfn_Alert("출력할 데이터가 존재하지 않습니다.");

        return false;

    }

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1201";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oExamJubsu.GetRowCount();
		parValue += " sTitle=" + quote(gdExamJubsu.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);

}



