function cbxCourse_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if(strcode == "10")
	{
		Static1.Visible = true;
		radJubsugubun2.Visible = true;
		radJubsugubun2.Index = 0;
	}
	else {
		Static1.Visible = false;
		radJubsugubun2.Visible = false;
		radJubsugubun2.Index = 0;
	}
}

function radSearchGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") {	
		edExamOrderStart.Enable = true;		
		edExamOrderEnd.Enable = true;		
		calExamStart.Enable = false;		
		calExamEnd.Enable = false;		
		edExamOrderStart.Value = "001";		
		edExamOrderEnd.Value = "001";		
		calExamStart.Value = "";		
		calExamEnd.Value = "";		
	} 
	else {	
		edExamOrderStart.Enable = false;		
		edExamOrderEnd.Enable = false;		
		calExamStart.Enable = true;		
		calExamEnd.Enable = true;		
		calExamStart.Value = left(getDate(),8);		
		calExamEnd.Value = left(getDate(),8);		
		edExamOrderStart.Value = "";		
		edExamOrderEnd.Value = "";		
	}	
}

