function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
		ls_FormId = split(ls_FormId[1], ".");
		
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } 
    else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } 
    else {
        gfn_Alert(MSGI00080);
        close();
    }    
    // 공통 코드 조회

	// 시험직능(E) / 수첩직능(L) / 강습직능(T)
    gfn_SearchTrainingCourse("T", ds_oCourse, "Y", "선택"); // 강습직능(T) 코드 조회
             
    /*  
    for(i = 0; i < ds_oCourse.RowCount(); i++) { // 업무대행 감독자, 건설현장 소방안전관리자 직능 제외

        if (ds_oCourse.GetColumn(i, "CD") == "30" ||
            ds_oCourse.GetColumn(i, "CD") == "31") {
            
			ds_oCourse.DeleteRow(i);
			i = i - 1;
			}
	} */
	
    cbxCourse.Value = "22";

	gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회  
    edExamOrder.SetFocus();    
    edExamYear.Text = left(GetDate(), 4);
}


function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    // to do
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}


function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        gdExamJubsu.Height = nCy - gdExamJubsu.Top - 4;
        gdExamJubsu.Width = nCx - gdExamJubsu.Left - 4;
    }
}


/****************************************************************************************
 * 기능 : 조회
 ****************************************************************************************/
function lfn_Search(obj) {       
        
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    var sortEOHEXAMSEATNO = "";                  // 정렬 순서를 선택하여 변수에 담아 null인 경우 변수를 null로 만듬
    var sortTPPERSONNM = "";                     // 쿼리 실행시 null이 아닌 것만 실행
    var sortEOHJUBSUNO = "";
    
    if ( radSort.value =="EOHEXAMSEATNO"){
        sortEOHEXAMSEATNO = "EOHEXAMSEATNO";       
        sortTPPERSONNM    = "";
        sortEOHJUBSUNO    = "";           
    } 
    else if( radSort.value == "TPPERSONNM"){
        sortEOHEXAMSEATNO = ""; 
        sortTPPERSONNM    = "TPPERSONNM";
        sortEOHJUBSUNO    = "";                   
    } 
    else {
        sortEOHEXAMSEATNO = "";
        sortTPPERSONNM    = "";
        sortEOHJUBSUNO    = "EOHJUBSUNO";               
    }
    ///////////////////// 조회이력 남기기
    ls_HistoryArg = "지부=" + quote(cbxJibu.value)   //-- 지부
              +" 년도=" + quote(edExamYear.text)  //-- 년도 
              +" 직능=" + quote(cbxCourse.value)   //-- 직능              
              +" 회차=" + quote(edExamOrder.text) // -- 회차             
              +" 정렬순서1=" + quote(sortEOHEXAMSEATNO) // -- 정렬순서
              +" 정렬순서2=" + quote(sortTPPERSONNM) // -- 정렬순서
              +" 정렬순서3=" + quote(sortEOHJUBSUNO); // -- 정렬순서    
      
        gfn_InsertSearchPrintHistory(
            "S"
            , this.ID
            , global.mnuLeft.cbxOpenForm.Text
            , ls_HistoryArg
        ); 
    /////////////////////  
        
    ds_ioExamJubsuList.ClearData(); // 데이타셋 초기화    
    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("training:searchExamJubsuList", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioExamJubsuList=ds_oExamJubsuList"	// outDs
		,"EOMGGTMGNO=" + quote(cbxJibu.value)   //-- 지부
              +" EOYEAR=" + quote(edExamYear.text)  //-- 년도 
              +" EOTCCOURSECD=" + quote(cbxCourse.value)   //-- 직능              
              +" EOEXAMORDER=" + quote(edExamOrder.text) // -- 회차             
              +" sortEOHEXAMSEATNO=" + quote(sortEOHEXAMSEATNO) // -- 정렬순서
              +" sortTPPERSONNM=" + quote(sortTPPERSONNM) // -- 정렬순서
              +" sortEOHJUBSUNO=" + quote(sortEOHJUBSUNO) // -- 정렬순서
    
              // args 
		,"lfn_AfterSearchExamJubsuList"
		,false
	);
}


/****************************************************************************************
 * 기능 : 출력
 ****************************************************************************************/
function lfn_Print(obj)  {  
    // to do Validation Check
    if(ds_ioExamJubsuList.count() == 0) {
        alert("출력하실 시험응시 인원이 없습니다. 다시 조회를 바랍니다.");
        return false;
    }    
    if(!Confirm("시험응시명부를 출력하시겠습니까? 응시명부 미리보기를 하실수 있습니다. 출력량이 많을 경우 선택적으로 출력 바랍니다.")){  //
        return false;
    }   
    ///////////////////// 출력이력 남기기
    ls_HistoryArg = "지부=" + quote(cbxJibu.value)   //-- 지부
              +" 년도=" + quote(edExamYear.text)  //-- 년도 
              +" 직능=" + quote(cbxCourse.value)   //-- 직능              
              +" 회차=" + quote(edExamOrder.text); // -- 회차             
     
	gfn_InsertSearchPrintHistory(
		"P"
		, this.ID
		, global.mnuLeft.cbxOpenForm.Text
		, ls_HistoryArg
	); 
    /////////////////////      
	gfn_CallReportPopup("frmtraining0418R01ExamAttendanceRegister.rex", "ds_ioExamJubsuList"
                        ,"ImagePath=" + GF_FILE_SERVER_URL + "kems");                        
	// 이미지 연동 위한 글로벌 변수 인 경로
	/*
	"TOMGJIBU=" + quote(cbxJibu.Value)
	+ " TCCOURSECD=" + quote(cbxCourse.Value)
	+ " TOYEAR=" + quote(edExamYear.Text)
	+ " THORDERSEQSTART=" + quote(edExamOrderStart.Text)
	+ " THORDERSEQEND=" + quote(edExamOrderEnd.Text)
	+ " TJREPAYYN=" + quote(radRepayYn.Value)
	+ " TJPASSYN=" + quote(radPassYn.Value));
	*/
}


/* 공통 필수 종료 */
/* 사용자 함수 */
/****************************************************************************************
 * 기능 : 결강자 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 ****************************************************************************************/
function lfn_AfterSearchExamJubsuList(nErrorCode, strErrorMsg) {
    
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdExamJubsu); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    medExamList.Value = ds_ioExamJubsuList.count();
}


/****************************************************************************************
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 ****************************************************************************************/
function lfn_AfterSaveExamJubsuList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    lfn_Search(); // 결강자 조회
}


